import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Link, useNavigate } from "react-router-dom";
import Container from "../components/ui/Container";
import Input from "../components/ui/Input";
import Button from "../components/ui/Button";
import { authService } from "../lib/api/authService";

const signupSchema = z.object({
  username: z.string().min(2, "用户名至少 2 位"),
  email: z.string().email("请输入有效邮箱"),
  password: z.string().min(6, "密码至少 6 位"),
  confirmPassword: z.string().min(6, "密码至少 6 位"),
  verificationCode: z.string().min(4, "请输入验证码")
}).refine((data) => data.password === data.confirmPassword, {
  message: "两次输入的密码不一致",
  path: ["confirmPassword"]
});

type SignupForm = z.infer<typeof signupSchema>;

const SignupPage = () => {
  const navigate = useNavigate();
  const [countdown, setCountdown] = useState(0);
  const [sendingCode, setSendingCode] = useState(false);
  const [apiError, setApiError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const {
    register,
    handleSubmit,
    watch,
    formState: { errors, isSubmitting }
  } = useForm<SignupForm>({
    resolver: zodResolver(signupSchema)
  });

  const email = watch("email");

  // 发送验证码
  const handleSendCode = async () => {
    setApiError("");

    // 验证邮箱格式
    if (!email || !email.includes("@")) {
      setApiError("请先输入有效的邮箱地址");
      return;
    }

    setSendingCode(true);
    try {
      const response = await authService.sendVerificationCode(email);

      if (response.code === 200) {
        // 开始倒计时
        setCountdown(60);
        const timer = setInterval(() => {
          setCountdown((prev) => {
            if (prev <= 1) {
              clearInterval(timer);
              return 0;
            }
            return prev - 1;
          });
        }, 1000);

        // 开发环境显示验证码
        if (response.data.dev_code) {
          console.log("开发环境验证码:", response.data.dev_code);
          setApiError(`开发环境验证码: ${response.data.dev_code}`);
        }
      }
    } catch (error: any) {
      setApiError(error.message || "发送验证码失败");
    } finally {
      setSendingCode(false);
    }
  };

  const onSubmit = async (values: SignupForm) => {
    setApiError("");
    setSuccessMessage("");

    try {
      // 调用注册 API
      const response = await authService.register(
        values.username,
        values.email,
        values.password,
        values.verificationCode
      );

      if (response.code === 200) {
        setSuccessMessage("注册成功！3 秒后跳转到登录页...");

        // 3 秒后跳转到登录页
        setTimeout(() => {
          navigate("/login");
        }, 3000);
      }
    } catch (error: any) {
      setApiError(error.message || "注册失败，请重试");
    }
  };

  return (
    <div data-scroll-section className="bg-bg-primary py-16 sm:py-20 lg:py-24">
      <Container size="narrow" className="max-w-md">
        <div className="rounded-md border border-border-main bg-bg-surface p-10 shadow-subtle">
          <header className="mb-8 space-y-2 text-center">
            <h1 className="text-3xl font-semibold text-text-head">注册账号</h1>
            <p className="text-sm text-text-body">
              创建账号后即可报名参加 BoHack 2025
            </p>
          </header>

          {/* 成功消息 */}
          {successMessage && (
            <div className="mb-6 rounded-md border border-success-green/60 bg-success-green/10 p-4 text-sm text-success-green">
              {successMessage}
            </div>
          )}

          {/* 错误消息 */}
          {apiError && (
            <div className="mb-6 rounded-md border border-error-red/60 bg-error-red/10 p-4 text-sm text-error-red">
              {apiError}
            </div>
          )}

          <form className="space-y-5" onSubmit={handleSubmit(onSubmit)} noValidate>
            <div className="space-y-2">
              <label className="text-sm font-medium text-text-head">用户名</label>
              <Input type="text" placeholder="请输入用户名" {...register("username")} />
              {errors.username && (
                <p className="text-xs text-error-red">{errors.username.message}</p>
              )}
            </div>

            <div className="space-y-2">
              <label className="text-sm font-medium text-text-head">邮箱</label>
              <Input type="email" placeholder="hello@example.com" {...register("email")} />
              {errors.email && (
                <p className="text-xs text-error-red">{errors.email.message}</p>
              )}
            </div>

            <div className="space-y-2">
              <label className="text-sm font-medium text-text-head">验证码</label>
              <div className="flex gap-2">
                <Input
                  type="text"
                  placeholder="请输入验证码"
                  {...register("verificationCode")}
                  className="flex-1"
                />
                <Button
                  type="button"
                  onClick={handleSendCode}
                  disabled={countdown > 0 || sendingCode}
                  className="w-32 shrink-0"
                  isLoading={sendingCode}
                >
                  {countdown > 0 ? `${countdown}秒` : "发送验证码"}
                </Button>
              </div>
              {errors.verificationCode && (
                <p className="text-xs text-error-red">{errors.verificationCode.message}</p>
              )}
            </div>

            <div className="space-y-2">
              <label className="text-sm font-medium text-text-head">密码</label>
              <Input type="password" placeholder="••••••" {...register("password")} />
              {errors.password && (
                <p className="text-xs text-error-red">{errors.password.message}</p>
              )}
            </div>

            <div className="space-y-2">
              <label className="text-sm font-medium text-text-head">确认密码</label>
              <Input type="password" placeholder="••••••" {...register("confirmPassword")} />
              {errors.confirmPassword && (
                <p className="text-xs text-error-red">{errors.confirmPassword.message}</p>
              )}
            </div>

            <Button type="submit" className="w-full" isLoading={isSubmitting}>
              注册
            </Button>
          </form>
          <p className="mt-6 text-center text-sm text-text-muted">
            已有账号？<Link to="/login" className="text-accent-purple">立即登录</Link>
          </p>
        </div>
      </Container>
    </div>
  );
};

export default SignupPage;

