import MuiCard from '@mui/material/Card';
import { styled } from '@mui/material/styles';
import { ThemeProvider } from '@mui/material/styles';
import { theme } from "../theme/muiTheme";
import { BoHackIcon } from "../components/auth/BoHackIcon";
import ForgotPasswordDialog from "../components/auth/ForgotPasswordDialog";
import { authService } from "../lib/api/authService";
import { useAuthStore } from "../stores/authStore";

const Card = styled(MuiCard)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignSelf: 'center',
  width: '100%',
  padding: theme.spacing(4),
  gap: theme.spacing(2),
  margin: 'auto',
  [theme.breakpoints.up('sm')]: {
    maxWidth: '450px',
  },
  boxShadow:
    'hsla(220, 30%, 5%, 0.05) 0px 5px 15px 0px, hsla(220, 25%, 10%, 0.05) 0px 15px 35px -5px',
}));

const SignInContainer = styled(Stack)(({ theme }) => ({
  minHeight: '100vh',
  padding: theme.spacing(2),
  [theme.breakpoints.up('sm')]: {
    padding: theme.spacing(4),
  },
  '&::before': {
    content: '""',
    display: 'block',
    position: 'absolute',
    zIndex: -1,
    inset: 0,
    backgroundImage:
      'radial-gradient(ellipse at 50% 50%, hsl(210, 100%, 97%), hsl(0, 0%, 100%))',
    backgroundRepeat: 'no-repeat',
  },
}));

const loginSchema = z.object({
  identifier: z.string().min(1, "请输入邮箱或用户名"),
  password: z.string().min(6, "密码至少 6 位")
});

type LoginForm = z.infer<typeof loginSchema>;

const LoginPage = () => {
  const navigate = useNavigate();
  const [apiError, setApiError] = useState("");
  const [forgotPasswordOpen, setForgotPasswordOpen] = useState(false);
  const setAuth = useAuthStore((state) => state.setAuth);

  const {
    control,
    handleSubmit,
    formState: { errors, isSubmitting }
  } = useForm<LoginForm>({
    resolver: zodResolver(loginSchema)
  });

  const onSubmit = async (values: LoginForm) => {
    setApiError("");

    try {
      const response = await authService.login(values.identifier, values.password);

      if (response.code === 200 || response.code === 0) {
        localStorage.setItem("access_token", response.data.access_token);
        localStorage.setItem("refresh_token", response.data.refresh_token);

        const userResponse = await authService.getProfile();

        if (userResponse.code === 200 || userResponse.code === 0) {
          setAuth(response.data, userResponse.data);
          navigate("/register");
        } else {
          setApiError("获取用户信息失败");
        }
      } else {
        setApiError(response.message || "登录失败");
      }
    } catch (error: any) {
      setApiError(error.message || "登录失败，请检查用户名和密码");
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline enableColorScheme />
      <SignInContainer direction="column" justifyContent="space-between">
        <Card variant="outlined">
          <BoHackIcon />
          <Typography
            component="h1"
            variant="h4"
            sx={{ width: '100%', fontSize: 'clamp(2rem, 10vw, 2.15rem)' }}
          >
            登录
          </Typography>

          {apiError && (
            <Box sx={{
              p: 2,
              bgcolor: 'error.light',
              color: 'error.dark',
              borderRadius: 1,
              fontSize: '0.875rem'
            }}>
              {apiError}
            </Box>
          )}

          <Box
            component="form"
            onSubmit={handleSubmit(onSubmit)}
            noValidate
            sx={{
              display: 'flex',
              flexDirection: 'column',
              width: '100%',
              gap: 2,
            }}
          >
            <FormControl>
              <FormLabel htmlFor="identifier">邮箱或用户名</FormLabel>
              <Controller
                name="identifier"
                control={control}
                defaultValue=""
                render={({ field }) => (
                  <TextField
                    {...field}
                    error={!!errors.identifier}
                    helperText={errors.identifier?.message}
                    id="identifier"
                    type="text"
                    placeholder="hello@example.com 或 username"
                    autoComplete="email"
                    autoFocus
                    required
                    fullWidth
                    variant="outlined"
                  />
                )}
              />
            </FormControl>
            <FormControl>
              <FormLabel htmlFor="password">密码</FormLabel>
              <Controller
                name="password"
                control={control}
                defaultValue=""
                render={({ field }) => (
                  <TextField
                    {...field}
                    error={!!errors.password}
                    helperText={errors.password?.message}
                    name="password"
                    placeholder="••••••"
                    type="password"
                    id="password"
                    autoComplete="current-password"
                    required
                    fullWidth
                    variant="outlined"
                  />
                )}
              />
            </FormControl>
            <ForgotPasswordDialog
              open={forgotPasswordOpen}
              handleClose={() => setForgotPasswordOpen(false)}
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              disabled={isSubmitting}
            >
              {isSubmitting ? '登录中...' : '登录'}
            </Button>
            <Link
              component="button"
              type="button"
              onClick={() => setForgotPasswordOpen(true)}
              variant="body2"
              sx={{ alignSelf: 'center' }}
            >
              忘记密码？
            </Link>
          </Box>
          <Typography sx={{ textAlign: 'center' }}>
            没有账号？{' '}
            <Link
              href="/signup"
              variant="body2"
              sx={{ alignSelf: 'center' }}
            >
              注册
            </Link>
          </Typography>
        </Card>
      </SignInContainer>
    </ThemeProvider>
