toolUseResult.content:
import { useState } from "react";
import { useForm, Controller } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useNavigate } from "react-router-dom";
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import FormLabel from '@mui/material/FormLabel';
import FormControl from '@mui/material/FormControl';
import Link from '@mui/material/Link';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import Stack from '@mui/material/Stack';
import MuiCard from '@mui/material/Card';
import { styled } from '@mui/material/styles';
import { ThemeProvider } from '@mui/material/styles';
import { theme } from "../theme/muiTheme";
import { BoHackIcon } from "../components/auth/BoHackIcon";
import { authService } from "../lib/api/authService";

const Card = styled(MuiCard)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignSelf: 'center',
  width: '100%',
  padding: theme.spacing(4),
  gap: theme.spacing(2),
  margin: 'auto',
  boxShadow:
    'hsla(220, 30%, 5%, 0.05) 0px 5px 15px 0px, hsla(220, 25%, 10%, 0.05) 0px 15px 35px -5px',
  [theme.breakpoints.up('sm')]: {
    width: '450px',
  },
}));

const SignUpContainer = styled(Stack)(({ theme }) => ({
  minHeight: '100vh',
  padding: theme.spacing(2),
  [theme.breakpoints.up('sm')]: {
    padding: theme.spacing(4),
  },
  '&::before': {
    content: '""',
    display: 'block',
    position: 'absolute',
    zIndex: -1,
    inset: 0,
    backgroundImage:
      'radial-gradient(ellipse at 50% 50%, hsl(210, 100%, 97%), hsl(0, 0%, 100%))',
    backgroundRepeat: 'no-repeat',
  },
}));

const signupSchema = z.object({
  username: z.string().min(2, "用户名至少 2 位"),
  email: z.string().email("请输入有效邮箱"),
  password: z.string().min(6, "密码至少 6 位"),
  confirmPassword: z.string().min(6, "密码至少 6 位"),
  verificationCode: z.string().min(4, "请输入验证码")
}).refine((data) => data.password === data.confirmPassword, {
  message: "两次输入的密码不一致",
  path: ["confirmPassword"]
});

type SignupForm = z.infer<typeof signupSchema>;

const SignupPage = () => {
  const navigate = useNavigate();
  const [countdown, setCountdown] = useState(0);
  const [sendingCode, setSendingCode] = useState(false);
  const [apiError, setApiError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const {
    control,
    handleSubmit,
    watch,
    formState: { errors, isSubmitting }
  } = useForm<SignupForm>({
    resolver: zodResolver(signupSchema)
  });

  const email = watch("email");

  const handleSendCode = async () => {
    setApiError("");

    if (!email || !email.includes("@")) {
      setApiError("请先输入有效的邮箱地址");
      return;
    }

    setSendingCode(true);
    try {
      const response = await authService.sendVerificationCode(email);

      if (response.code === 200) {
        setCountdown(60);
        const timer = setInterval(() => {
          setCountdown((prev) => {
            if (prev <= 1) {
              clearInterval(timer);
              return 0;
            }
            return prev - 1;
          });
        }, 1000);

        if (response.data.dev_code) {
          console.log("开发环境验证码:", response.data.dev_code);
          setApiError(`开发环境验证码: ${response.data.dev_code}`);
        }
      }
    } catch (error: any) {
      setApiError(error.message || "发送验证码失败");
    } finally {
      setSendingCode(false);
    }
  };

  const onSubmit = async (values: SignupForm) => {
    setApiError("");
    setSuccessMessage("");

    try {
      const response = await authService.register(
        values.username,
        values.email,
        values.password,
        values.verificationCode
      );

      if (response.code === 200) {
        setSuccessMessage("注册成功！3 秒后跳转到登录页...");

        setTimeout(() => {
          navigate("/login");
        }, 3000);
      }
    } catch (error: any) {
      setApiError(error.message || "注册失败，请重试");
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline enableColorScheme />
      <SignUpContainer direction="column" justifyContent="space-between">
        <Card variant="outlined">
          <BoHackIcon />
          <Typography
            component="h1"
            variant="h4"
            sx={{ width: '100%', fontSize: 'clamp(2rem, 10vw, 2.15rem)' }}
          >
            注册
          </Typography>

          {successMessage && (
            <Box sx={{
              p: 2,
              bgcolor: 'success.light',
              color: 'success.dark',
              borderRadius: 1,
              fontSize: '0.875rem'
            }}>
              {successMessage}
            </Box>
          )}

          {apiError && (
            <Box sx={{
              p: 2,
              bgcolor: 'error.light',
              color: 'error.dark',
              borderRadius: 1,
              fontSize: '0.875rem'
            }}>
              {apiError}
            </Box>
          )}

          <Box
            component="form"
            onSubmit={handleSubmit(onSubmit)}
            sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}
          >
            <FormControl>
              <FormLabel htmlFor="username">用户名</FormLabel>
              <Controller
                name="username"
                control={control}
                defaultValue=""
                render={({ field }) => (
                  <TextField
                    {...field}
                    autoComplete="username"
                    required
                    fullWidth
                    id="username"
                    placeholder="请输入用户名"
                    error={!!errors.username}
                    helperText={errors.username?.message}
                  />
                )}
              />
            </FormControl>

            <FormControl>
              <FormLabel htmlFor="email">邮箱</FormLabel>
              <Controller
                name="email"
                control={control}
                defaultValue=""
                render={({ field }) => (
                  <TextField
                    {...field}
                    required
                    fullWidth
                    id="email"
                    placeholder="your@email.com"
                    autoComplete="email"
                    variant="outlined"
                    error={!!errors.email}
                    helperText={errors.email?.message}
                  />
                )}
              />
            </FormControl>

            <FormControl>
              <FormLabel htmlFor="verificationCode">验证码</FormLabel>
              <Box sx={{ display: 'flex', gap: 1 }}>
                <Controller
                  name="verificationCode"
                  control={control}
                  defaultValue=""
                  render={({ field }) => (
                    <TextField
                      {...field}
                      required
                      id="verificationCode"
                      placeholder="请输入验证码"
                      variant="outlined"
                      error={!!errors.verificationCode}
                      helperText={errors.verificationCode?.message}
                      sx={{ flex: 1 }}
                    />
                  )}
                />
                <Button
                  variant="outlined"
                  onClick={handleSendCode}
                  disabled={countdown > 0 || sendingCode}
                  sx={{ minWidth: '120px' }}
                >
                  {sendingCode ? '发送中...' : countdown > 0 ? `${countdown}秒` : '发送验证码'}
                </Button>
              </Box>
            </FormControl>

            <FormControl>
              <FormLabel htmlFor="password">密码</FormLabel>
              <Controller
                name="password"
                control={control}
                defaultValue=""
                render={({ field }) => (
                  <TextField
                    {...field}
                    required
                    fullWidth
                    placeholder="••••••"
                    type="password"
                    id="password"
                    autoComplete="new-password"
                    variant="outlined"
                    error={!!errors.password}
                    helperText={errors.password?.message}
                  />
                )}
              />
            </FormControl>

            <FormControl>
              <FormLabel htmlFor="confirmPassword">确认密码</FormLabel>
              <Controller
                name="confirmPassword"
                control={control}
                defaultValue=""
                render={({ field }) => (
                  <TextField
                    {...field}
                    required
                    fullWidth
                    placeholder="••••••"
                    type="password"
                    id="confirmPassword"
                    autoComplete="new-password"
                    variant="outlined"
                    error={!!errors.confirmPassword}
                    helperText={errors.confirmPassword?.message}
                  />
                )}
              />
            </FormControl>

            <Button
              type="submit"
              fullWidth
              variant="contained"
              disabled={isSubmitting}
            >
              {isSubmitting ? '注册中...' : '注册'}
            </Button>
          </Box>

          <Typography sx={{ textAlign: 'center' }}>
            已有账号？{' '}
            <Link
              href="/login"
              variant="body2"
              sx={{ alignSelf: 'center' }}
            >
              登录
            </Link>
          </Typography>
        </Card>
      </SignUpContainer>
    </ThemeProvider>
  );
};

export default SignupPage;

