import { useState } from "react";
import { useNavigate } from "react-router-dom";
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import FormLabel from '@mui/material/FormLabel';
import FormControl from '@mui/material/FormControl';
import Link from '@mui/material/Link';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import Stack from '@mui/material/Stack';
import MuiCard from '@mui/material/Card';
import { styled, ThemeProvider, createTheme } from '@mui/material/styles';
import { BoHackIcon } from "../components/auth/BoHackIcon";
import ForgotPasswordDialog from "../components/auth/ForgotPasswordDialog";
import { authService } from "../lib/api/authService";
import { useAuthStore } from "../stores/authStore";

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: { main: '#4876EF' },
    background: {
      paper: 'rgba(30, 30, 30, 0.7)',
      default: '#0a1929',
    },
  },
});

const Card = styled(MuiCard)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignSelf: 'center',
  width: '100%',
  padding: theme.spacing(4),
  gap: theme.spacing(2),
  margin: 'auto',
  backgroundColor: 'rgba(30, 30, 30, 0.7)',
  backdropFilter: 'blur(10px)',
  [theme.breakpoints.up('sm')]: {
    maxWidth: '450px',
  },
  boxShadow: 'hsla(220, 30%, 5%, 0.05) 0px 5px 15px 0px, hsla(220, 25%, 10%, 0.05) 0px 15px 35px -5px',
  '& .MuiTypography-root': {
    color: 'rgba(255, 255, 255, 0.95)',
  },
  '& .MuiFormLabel-root': {
    color: 'rgba(255, 255, 255, 0.85)',
  },
  '& .MuiInputBase-root': {
    color: 'rgba(255, 255, 255, 0.95)',
    backgroundColor: 'rgba(0, 0, 0, 0.2)',
  },
  '& .MuiOutlinedInput-notchedOutline': {
    borderColor: 'rgba(255, 255, 255, 0.23)',
  },
  '& .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline': {
    borderColor: 'rgba(255, 255, 255, 0.4)',
  },
  '& .MuiButton-contained': {
    backgroundColor: '#e0e0e0',
    color: '#000',
    '&:hover': {
      backgroundColor: '#f5f5f5',
    },
  },
  '& .MuiLink-root': {
    color: 'rgba(255, 255, 255, 0.7)',
  },
}));

const SignInContainer = styled(Stack)(({ theme }) => ({
  height: 'calc((1 - var(--template-frame-height, 0)) * 100dvh)',
  minHeight: '100%',
  padding: theme.spacing(2),
  [theme.breakpoints.up('sm')]: {
    padding: theme.spacing(4),
  },
  '&::before': {
    content: '""',
    display: 'block',
    position: 'absolute',
    zIndex: -1,
    inset: 0,
    backgroundImage: 'radial-gradient(ellipse at 50% 50%, hsl(210, 100%, 97%), hsl(0, 0%, 100%))',
    backgroundRepeat: 'no-repeat',
  },
}));

export default function LoginPage() {
  const navigate = useNavigate();
  const setAuth = useAuthStore((state) => state.setAuth);
  const [identifierError, setIdentifierError] = useState(false);
  const [identifierErrorMessage, setIdentifierErrorMessage] = useState('');
  const [passwordError, setPasswordError] = useState(false);
  const [passwordErrorMessage, setPasswordErrorMessage] = useState('');
  const [open, setOpen] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleClickOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const validateInputs = () => {
    const identifier = document.getElementById('identifier') as HTMLInputElement;
    const password = document.getElementById('password') as HTMLInputElement;
    let isValid = true;

    if (!identifier.value || identifier.value.length < 1) {
      setIdentifierError(true);
      setIdentifierErrorMessage('请输入邮箱或用户名');
      isValid = false;
    } else {
      setIdentifierError(false);
      setIdentifierErrorMessage('');
    }

    if (!password.value || password.value.length < 6) {
      setPasswordError(true);
      setPasswordErrorMessage('密码至少 6 位');
      isValid = false;
    } else {
      setPasswordError(false);
      setPasswordErrorMessage('');
    }

    return isValid;
  };

  const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    if (!validateInputs()) return;

    setIsSubmitting(true);
    const data = new FormData(event.currentTarget);
    const identifier = data.get('identifier') as string;
    const password = data.get('password') as string;

    try {
      const response = await authService.login(identifier, password);
      if (response.code === 200 || response.code === 0) {
        localStorage.setItem("access_token", response.data.access_token);
        localStorage.setItem("refresh_token", response.data.refresh_token);
        const userResponse = await authService.getProfile();
        if (userResponse.code === 200 || userResponse.code === 0) {
          setAuth(response.data, userResponse.data);
          navigate("/register");
        } else {
          setIdentifierErrorMessage("获取用户信息失败");
          setIdentifierError(true);
        }
      } else {
        setIdentifierErrorMessage(response.message || "登录失败");
        setIdentifierError(true);
      }
    } catch (error: any) {
      setIdentifierErrorMessage(error.message || "登录失败，请检查用户名和密码");
      setIdentifierError(true);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline enableColorScheme />
      <SignInContainer direction="column" justifyContent="space-between">
        <Card variant="outlined">
          <BoHackIcon />
          <Typography component="h1" variant="h4" sx={{ width: '100%', fontSize: 'clamp(2rem, 10vw, 2.15rem)' }}>
            登录
          </Typography>
          <Box component="form" onSubmit={handleSubmit} noValidate sx={{ display: 'flex', flexDirection: 'column', width: '100%', gap: 2 }}>
            <FormControl>
              <FormLabel htmlFor="identifier">邮箱或用户名</FormLabel>
              <TextField
                error={identifierError}
                helperText={identifierErrorMessage}
                id="identifier"
                type="text"
                name="identifier"
                placeholder="hello@example.com 或 username"
                autoComplete="email"
                autoFocus
                required
                fullWidth
                variant="outlined"
                color={identifierError ? 'error' : 'primary'}
              />
            </FormControl>
            <FormControl>
              <FormLabel htmlFor="password">密码</FormLabel>
              <TextField
                error={passwordError}
                helperText={passwordErrorMessage}
                name="password"
                placeholder="••••••"
                type="password"
                id="password"
                autoComplete="current-password"
                required
                fullWidth
                variant="outlined"
                color={passwordError ? 'error' : 'primary'}
              />
            </FormControl>
            <ForgotPasswordDialog open={open} handleClose={handleClose} />
            <Button type="submit" fullWidth variant="contained" disabled={isSubmitting}>
              {isSubmitting ? '登录中...' : '登录'}
            </Button>
            <Link component="button" type="button" onClick={handleClickOpen} variant="body2" sx={{ alignSelf: 'center' }}>
              忘记密码？
            </Link>
          </Box>
          <Typography sx={{ textAlign: 'center' }}>
            没有账号？{' '}
            <Link href="/signup" variant="body2" sx={{ alignSelf: 'center' }}>
              注册
            </Link>
          </Typography>
        </Card>
      </SignInContainer>
    </ThemeProvider>
  );
}

