    const password = document.getElementById('password') as HTMLInputElement;
    let isValid = true;

    if (!identifier.value || identifier.value.length < 1) {
      setIdentifierError(true);
      setIdentifierErrorMessage('请输入邮箱或用户名');
      isValid = false;
    } else {
      setIdentifierError(false);
      setIdentifierErrorMessage('');
    }

    if (!password.value || password.value.length < 6) {
      setPasswordError(true);
      setPasswordErrorMessage('密码至少 6 位');
      isValid = false;
    } else {
      setPasswordError(false);
      setPasswordErrorMessage('');
    }

    return isValid;
  };

  const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    if (!validateInputs()) return;

    setIsSubmitting(true);
    const data = new FormData(event.currentTarget);
    const identifier = data.get('identifier') as string;
    const password = data.get('password') as string;

    try {
      const response = await authService.login(identifier, password);
      if (response.code === 200 || response.code === 0) {
        localStorage.setItem("access_token", response.data.access_token);
        localStorage.setItem("refresh_token", response.data.refresh_token);
        const userResponse = await authService.getProfile();
        if (userResponse.code === 200 || userResponse.code === 0) {
          setAuth(response.data, userResponse.data);
          navigate("/register");
        } else {
          setIdentifierErrorMessage("获取用户信息失败");
          setIdentifierError(true);
        }
      } else {
        setIdentifierErrorMessage(response.message || "登录失败");
        setIdentifierError(true);
      }
    } catch (error: any) {
      setIdentifierErrorMessage(error.message || "登录失败，请检查用户名和密码");
      setIdentifierError(true);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline enableColorScheme />
      <SignInContainer direction="column" justifyContent="space-between">
        <Card variant="outlined">
          <BoHackIcon />
          <Typography component="h1" variant="h4" sx={{ width: '100%', fontSize: 'clamp(2rem, 10vw, 2.15rem)' }}>
            登录
          </Typography>
          <Box component="form" onSubmit={handleSubmit} noValidate sx={{ display: 'flex', flexDirection: 'column', width: '100%', gap: 2 }}>
            <FormControl>
              <FormLabel htmlFor="identifier">邮箱或用户名</FormLabel>
              <TextField
                error={identifierError}
                helperText={identifierErrorMessage}
                id="identifier"
                type="text"
                name="identifier"
                placeholder="hello@example.com 或 username"
                autoComplete="email"
                autoFocus
                required
                fullWidth
                variant="outlined"
                color={identifierError ? 'error' : 'primary'}
              />
            </FormControl>
            <FormControl>
              <FormLabel htmlFor="password">密码</FormLabel>
              <TextField
                error={passwordError}
                helperText={passwordErrorMessage}
                name="password"
                placeholder="••••••"
                type="password"
                id="password"
                autoComplete="current-password"
                required
                fullWidth
                variant="outlined"
                color={passwordError ? 'error' : 'primary'}
              />
            </FormControl>
            <ForgotPasswordDialog open={open} handleClose={handleClose} />
            <Button type="submit" fullWidth variant="contained" disabled={isSubmitting}>
              {isSubmitting ? '登录中...' : '登录'}
            </Button>
            <Link component="button" type="button" onClick={handleClickOpen} variant="body2" sx={{ alignSelf: 'center' }}>
              忘记密码？
            </Link>
          </Box>
          <Typography sx={{ textAlign: 'center' }}>
            没有账号？{' '}
            <Link href="/signup" variant="body2" sx={{ alignSelf: 'center' }}>
              注册
            </Link>
          </Typography>
        </Card>
      </SignInContainer>
    </ThemeProvider>
  );
}

