import { useState, useEffect } from "react";
import { useForm, useFieldArray } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import Container from "../components/ui/Container";
import Input from "../components/ui/Input";
import Textarea from "../components/ui/Textarea";
import Button from "../components/ui/Button";
import StepIndicator from "../components/ui/StepIndicator";
import FileUpload from "../components/ui/FileUpload";
import { registrationService } from "../lib/api/registrationService";
import { uploadService } from "../lib/api/uploadService";
import { useAuthStore } from "../stores/authStore";
import {
  registrationFormSchema,
  basicInfoSchema,
  skillsSchema,
  reflectionSchema,
  question1Options,
  question2Options,
  type RegistrationFormData,
} from "../schemas/registrationSchema";
import type { RegistrationNote } from "../types/api";
import { Plus, Trash2 } from "lucide-react";

const RegisterPage = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [submitted, setSubmitted] = useState(false);
  const [apiError, setApiError] = useState("");
  const [existingRegistration, setExistingRegistration] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [uploadingFile, setUploadingFile] = useState(false);
  const [uploadedFileName, setUploadedFileName] = useState<string>("");
  const user = useAuthStore((state) => state.user);

  const {
    register,
    handleSubmit,
    formState: { errors, isSubmitting },
    reset,
    setValue,
    watch,
    trigger,
    control,
  } = useForm<RegistrationFormData>({
    resolver: zodResolver(registrationFormSchema),
    defaultValues: {
      gender: "male",
      pastActivities: [],
      question1: { question: "", answer: "" },
      question2: { question: "", answer: "" },
    },
    mode: "onChange",
  });

  const { fields, append, remove } = useFieldArray({
    control,
    name: "pastActivities",
  });

  // 检查是否已报名
  useEffect(() => {
    const checkRegistrationStatus = async () => {
      try {
        const response = await registrationService.getRegistrationStatus();
        if (response.code === 200 && response.data) {
          setExistingRegistration(response.data);
        }
      } catch (error) {
        console.log("No existing registration");
      } finally {
        setLoading(false);
      }
    };

    checkRegistrationStatus();
  }, []);

  // 自动填充用户邮箱
  useEffect(() => {
    if (user?.email) {
      setValue("email", user.email);
    }
  }, [user, setValue]);

  // 文件上传处理
  const handleFileSelect = async (file: File) => {
    setUploadingFile(true);
    setApiError("");

    try {
      const response = await uploadService.uploadFile(file);
      if (response.code === 200) {
        setValue("resumeUrl", response.data.url);
        setUploadedFileName(file.name);
      }
    } catch (error: any) {
      setApiError(error.message || "文件上传失败");
    } finally {
      setUploadingFile(false);
    }
  };

  const handleFileRemove = () => {
    setValue("resumeUrl", undefined);
    setUploadedFileName("");
  };

  // 步骤验证和导航
  const handleNextStep = async () => {
    let fieldsToValidate: any[] = [];

    if (currentStep === 1) {
      fieldsToValidate = [
        "nickname",
        "fullName",
        "gender",
        "birthDate",
        "school",
        "phone",
        "email",
        "hearAbout",
      ];
    } else if (currentStep === 2) {
      fieldsToValidate = ["technicalSkills"];
    }

    const isValid = await trigger(fieldsToValidate as any);
    if (isValid) {
      setCurrentStep(currentStep + 1);
      window.scrollTo({ top: 0, behavior: "smooth" });
    }
  };

  const handlePrevStep = () => {
    setCurrentStep(currentStep - 1);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  // 表单提交
  const onSubmit = async (values: RegistrationFormData) => {
    setApiError("");

    if (!user?.userId) {
      setApiError("用户信息不完整，请重新登录");
      return;
    }

    try {
      // 构建 JSON note 结构
      const note: RegistrationNote = {
        basicInfo: {
          nickname: values.nickname,
          fullName: values.fullName,
          gender: values.gender,
          birthDate: values.birthDate,
          school: values.school,
          phone: values.phone,
          email: values.email,
          resumeUrl: values.resumeUrl,
          hearAbout: values.hearAbout,
        },
        skills: {
          technicalSkills: values.technicalSkills,
          pastActivities: values.pastActivities,
          portfolioLinks: values.portfolioLinks,
        },
        reflections: {
          question1: values.question1,
          question2: values.question2,
          question3: values.question3,
        },
      };

      // 映射到 API 格式 - 需要 uid, description
      const apiData = {
        uid: user.userId,
        description: JSON.stringify(note),
      };

      console.log("提交报名数据:", apiData);
      console.log("用户ID:", user.userId, "类型:", typeof user.userId);
      console.log("description长度:", apiData.description.length);

      const response = await registrationService.submitRegistration(apiData);
      console.log("报名响应:", response);

      if (response.code === 200 || response.code === 0) {
        setSubmitted(true);
        setExistingRegistration(response.data);
        reset();
        setCurrentStep(1);
      }
    } catch (error: any) {
      console.error("报名错误:", error);

      // 显示详细的验证错误
      if (error.data && Array.isArray(error.data) && error.data.length > 0) {
        const errorMessages = error.data.map((err: any) => err.msg || err.message || JSON.stringify(err)).join(", ");
        console.error("详细错误:", error.data);
        setApiError(`参数校验失败: ${errorMessages}`);
      } else {
        setApiError(error.message || "报名失败，请重试");
      }
    }
  };

  // 如果正在加载
  if (loading) {
    return (
      <div data-scroll-section className="bg-bg-secondary py-16 sm:py-20 lg:py-24">
        <Container size="narrow">
          <div className="rounded-md border border-border-main bg-bg-surface p-10 text-center shadow-subtle">
            <p className="text-text-body">加载中...</p>
          </div>
        </Container>
      </div>
    );
  }

  // 如果已经报名
  if (existingRegistration && !submitted) {
    return (
      <div data-scroll-section className="bg-bg-secondary py-16 sm:py-20 lg:py-24">
        <Container size="narrow">
          <div className="rounded-md border border-border-main bg-bg-surface p-10 shadow-subtle">
            <header className="mb-8 space-y-3 text-center">
              <h1 className="text-3xl font-semibold text-text-head sm:text-4xl">
                报名状态
              </h1>
            </header>
            <div className="space-y-6">
              <div className="rounded-md border border-success-green/60 bg-success-green/10 p-6">
                <h2 className="mb-4 text-lg font-semibold text-success-green">
                  您已成功报名 BoHack 2025！
                </h2>
                <div className="space-y-2 text-sm text-text-body">
                  <p><strong>团队名称：</strong>{existingRegistration.teamName}</p>
                  <p><strong>角色：</strong>{existingRegistration.leader ? "队长" : "队员"}</p>
                  {existingRegistration.status && (
                    <p><strong>状态：</strong>{existingRegistration.status}</p>
                  )}
                </div>
              </div>
              <p className="text-center text-sm text-text-muted">
                请留意邮箱，组委会将会尽快与你联系。
              </p>
            </div>
          </div>
        </Container>
      </div>
    );
  }

  return (
    <div data-scroll-section className="bg-bg-secondary py-16 sm:py-20 lg:py-24">
      <Container size="narrow">
        <div className="rounded-md border border-border-main bg-bg-surface p-10 shadow-subtle">
          <header className="mb-10 space-y-3 text-center">
            <h1 className="text-3xl font-semibold text-text-head sm:text-4xl">
              报名参加 BoHack 2025
            </h1>
            <p className="text-base text-text-body">
              填写以下信息，我们会在 3 个工作日内发送审核结果和下一步指引。
            </p>
          </header>

          {/* 步骤指示器 */}
          <StepIndicator
            currentStep={currentStep}
            totalSteps={3}
            steps={["基本信息", "技能经历", "思考问题"]}
          />

          {/* 错误消息 */}
          {apiError && (
            <div className="mb-8 rounded-md border border-error-red/60 bg-error-red/10 p-4 text-sm text-error-red">
              {apiError}
            </div>
          )}

          <form onSubmit={handleSubmit(onSubmit)} noValidate>
            {/* Page 1: Basic Information */}
            {currentStep === 1 && (
              <div className="space-y-6">
                <div className="grid gap-6 sm:grid-cols-2">
                  <div className="space-y-2">
                    <label className="text-sm font-medium text-text-head">
                      昵称 <span className="text-error-red">*</span>
                    </label>
                    <Input placeholder="你的昵称" {...register("nickname")} />
                    {errors.nickname && (
                      <p className="text-xs text-error-red">{errors.nickname.message}</p>
                    )}
                  </div>

                  <div className="space-y-2">
                    <label className="text-sm font-medium text-text-head">
                      真实姓名 <span className="text-error-red">*</span>
                    </label>
                    <Input placeholder="你的真实姓名" {...register("fullName")} />
                    {errors.fullName && (
                      <p className="text-xs text-error-red">{errors.fullName.message}</p>
                    )}
                  </div>

                  <div className="space-y-2">
                    <label className="text-sm font-medium text-text-head">
                      性别 <span className="text-error-red">*</span>
                    </label>
                    <select
                      className="h-12 w-full rounded-md border border-border-main bg-bg-surface px-4 text-sm text-text-body focus:border-accent-purple focus:outline-none focus:ring-0"
                      {...register("gender")}
                    >
                      <option value="male">男</option>
                      <option value="female">女</option>
                      <option value="other">其他</option>
                    </select>
                    {errors.gender && (
                      <p className="text-xs text-error-red">{errors.gender.message}</p>
                    )}
                  </div>

                  <div className="space-y-2">
                    <label className="text-sm font-medium text-text-head">
                      出生日期 <span className="text-error-red">*</span>
                    </label>
                    <Input type="date" {...register("birthDate")} />
                    {errors.birthDate && (
                      <p className="text-xs text-error-red">{errors.birthDate.message}</p>
                    )}
                  </div>

                  <div className="space-y-2">
                    <label className="text-sm font-medium text-text-head">
                      学校 <span className="text-error-red">*</span>
                    </label>
                    <Input placeholder="你的学校名称" {...register("school")} />
                    {errors.school && (
                      <p className="text-xs text-error-red">{errors.school.message}</p>
                    )}
                  </div>

                  <div className="space-y-2">
                    <label className="text-sm font-medium text-text-head">
                      手机号 <span className="text-error-red">*</span>
                    </label>
                    <Input placeholder="138 0000 0000" {...register("phone")} />
                    {errors.phone && (
                      <p className="text-xs text-error-red">{errors.phone.message}</p>
                    )}
                  </div>

                  <div className="space-y-2 sm:col-span-2">
                    <label className="text-sm font-medium text-text-head">
                      邮箱 <span className="text-error-red">*</span>
                    </label>
                    <Input placeholder="hello@example.com" {...register("email")} />
                    {errors.email && (
                      <p className="text-xs text-error-red">{errors.email.message}</p>
                    )}
                  </div>
                </div>

                <div className="space-y-2">
                  <label className="text-sm font-medium text-text-head">
                    简历上传（可选）
                  </label>
                  <FileUpload
                    onFileSelect={handleFileSelect}
                    onFileRemove={handleFileRemove}
                    uploadedFileName={uploadedFileName}
                    isUploading={uploadingFile}
                  />
                </div>

                <div className="space-y-2">
                  <label className="text-sm font-medium text-text-head">
                    如何了解到本次活动 <span className="text-error-red">*</span>
                  </label>
                  <Textarea
                    rows={3}
                    placeholder="请告诉我们您是如何了解到本次活动的"
                    {...register("hearAbout")}
                  />
                  {errors.hearAbout && (
                    <p className="text-xs text-error-red">{errors.hearAbout.message}</p>
                  )}
                </div>

                <div className="flex justify-end">
                  <Button type="button" onClick={handleNextStep}>
                    下一步
                  </Button>
                </div>
              </div>
            )}

            {/* Page 2: Skills */}
            {currentStep === 2 && (
              <div className="space-y-6">
                <div className="space-y-2">
                  <label className="text-sm font-medium text-text-head">
                    技术技能 <span className="text-error-red">*</span>
                  </label>
                  <Textarea
                    rows={4}
                    placeholder="请描述您的技术技能，例如：编程语言、框架、工具等"
                    {...register("technicalSkills")}
                  />
                  {errors.technicalSkills && (
                    <p className="text-xs text-error-red">
                      {errors.technicalSkills.message}
                    </p>
                  )}
                </div>

                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <label className="text-sm font-medium text-text-head">
                      过往活动/项目经历（可选）
                    </label>
                    <Button
                      type="button"
                      variant="outline"
                      size="sm"
                      onClick={() => append({ name: "", description: "" })}
                    >
                      <Plus className="mr-1 h-4 w-4" />
                      添加
                    </Button>
                  </div>

                  {fields.map((field, index) => (
                    <div
                      key={field.id}
                      className="space-y-3 rounded-lg border border-border-main bg-bg-primary p-4"
                    >
                      <div className="flex items-center justify-between">
                        <h4 className="text-sm font-medium text-text-head">
                          活动/项目 {index + 1}
                        </h4>
                        <button
                          type="button"
                          onClick={() => remove(index)}
                          className="text-error-red hover:text-error-red/80"
                        >
                          <Trash2 className="h-4 w-4" />
                        </button>
                      </div>
                      <div className="space-y-2">
                        <Input
                          placeholder="活动/项目名称"
                          {...register(`pastActivities.${index}.name`)}
                        />
                        {errors.pastActivities?.[index]?.name && (
                          <p className="text-xs text-error-red">
                            {errors.pastActivities[index]?.name?.message}
                          </p>
                        )}
                      </div>
                      <div className="space-y-2">
                        <Textarea
                          rows={3}
                          placeholder="描述"
                          {...register(`pastActivities.${index}.description`)}
                        />
                        {errors.pastActivities?.[index]?.description && (
                          <p className="text-xs text-error-red">
                            {errors.pastActivities[index]?.description?.message}
                          </p>
                        )}
                      </div>
                    </div>
                  ))}
                </div>

                <div className="space-y-2">
                  <label className="text-sm font-medium text-text-head">
                    作品集链接（可选）
                  </label>
                  <Textarea
                    rows={3}
                    placeholder="请提供您的作品集、GitHub、个人网站等链接"
                    {...register("portfolioLinks")}
                  />
                </div>

                <div className="flex justify-between">
                  <Button type="button" variant="outline" onClick={handlePrevStep}>
                    上一步
                  </Button>
                  <Button type="button" onClick={handleNextStep}>
                    下一步
                  </Button>
                </div>
              </div>
            )}

            {/* Page 3: Reflection Questions */}
            {currentStep === 3 && (
              <div className="space-y-6">
                <div className="space-y-4">
                  <label className="text-sm font-medium text-text-head">
                    思考问题 1 <span className="text-error-red">*</span>
                  </label>
                  <select
                    className="h-12 w-full rounded-md border border-border-main bg-bg-surface px-4 text-sm text-text-body focus:border-accent-purple focus:outline-none focus:ring-0"
                    {...register("question1.question")}
                  >
                    <option value="">请选择一个问题</option>
                    {question1Options.map((q) => (
                      <option key={q} value={q}>
                        {q}
                      </option>
                    ))}
                  </select>
                  {errors.question1?.question && (
                    <p className="text-xs text-error-red">
                      {errors.question1.question.message}
                    </p>
                  )}
                  <Textarea
                    rows={5}
                    placeholder="请输入您的回答"
                    {...register("question1.answer")}
                  />
                  {errors.question1?.answer && (
                    <p className="text-xs text-error-red">
                      {errors.question1.answer.message}
                    </p>
                  )}
                </div>

                <div className="space-y-4">
                  <label className="text-sm font-medium text-text-head">
                    思考问题 2 <span className="text-error-red">*</span>
                  </label>
                  <select
                    className="h-12 w-full rounded-md border border-border-main bg-bg-surface px-4 text-sm text-text-body focus:border-accent-purple focus:outline-none focus:ring-0"
                    {...register("question2.question")}
                  >
                    <option value="">请选择一个问题</option>
                    {question2Options.map((q) => (
                      <option key={q} value={q}>
                        {q}
                      </option>
                    ))}
                  </select>
                  {errors.question2?.question && (
                    <p className="text-xs text-error-red">
                      {errors.question2.question.message}
                    </p>
                  )}
                  <Textarea
                    rows={5}
                    placeholder="请输入您的回答"
                    {...register("question2.answer")}
                  />
                  {errors.question2?.answer && (
                    <p className="text-xs text-error-red">
                      {errors.question2.answer.message}
                    </p>
                  )}
                </div>

                <div className="space-y-2">
                  <label className="text-sm font-medium text-text-head">
                    推荐朋友（可选）
                  </label>
                  <Textarea
                    rows={3}
                    placeholder="如果您想推荐朋友参加，请在此填写"
                    {...register("question3")}
                  />
                </div>

                <div className="flex justify-between">
                  <Button type="button" variant="outline" onClick={handlePrevStep}>
                    上一步
                  </Button>
                  <Button type="submit" isLoading={isSubmitting}>
                    提交报名
                  </Button>
                </div>
              </div>
            )}
          </form>
        </div>
      </Container>
    </div>
  );
};

export default RegisterPage;

input.content:
import { useState, useEffect } from "react";
import { useForm, useFieldArray } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import Stack from '@mui/material/Stack';
import MuiCard from '@mui/material/Card';
import FormLabel from '@mui/material/FormLabel';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import IconButton from '@mui/material/IconButton';
import Alert from '@mui/material/Alert';
import Stepper from '@mui/material/Stepper';
import Step from '@mui/material/Step';
import StepLabel from '@mui/material/StepLabel';
import { styled } from '@mui/material/styles';
import { Plus, Trash2 } from "lucide-react";
import AppTheme from "../theme/AppTheme";
import { registrationService } from "../lib/api/registrationService";
import { uploadService } from "../lib/api/uploadService";
import { useAuthStore } from "../stores/authStore";
import {
  registrationFormSchema,
  question1Options,
  question2Options,
  type RegistrationFormData,
} from "../schemas/registrationSchema";
import type { RegistrationNote } from "../types/api";

const Card = styled(MuiCard)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignSelf: 'center',
  width: '100%',
  padding: theme.spacing(4),
  gap: theme.spacing(2),
  margin: 'auto',
  maxWidth: '800px',
  boxShadow: 'hsla(220, 30%, 5%, 0.05) 0px 5px 15px 0px, hsla(220, 25%, 10%, 0.05) 0px 15px 35px -5px',
}));

const Container = styled(Stack)(() => ({
  minHeight: '100vh',
  padding: '2rem',
  backgroundImage: 'radial-gradient(at 50% 50%, hsla(210, 100%, 16%, 0.5), hsl(220, 30%, 5%))',
  backgroundRepeat: 'no-repeat',
}));

const RegisterPage = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [apiError, setApiError] = useState("");
  const [existingRegistration, setExistingRegistration] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [uploadingFile, setUploadingFile] = useState(false);
  const [uploadedFileName, setUploadedFileName] = useState<string>("");
  const user = useAuthStore((state) => state.user);

  const {
    register,
    handleSubmit,
    formState: { errors, isSubmitting },
    reset,
    setValue,
    trigger,
    control,
  } = useForm<RegistrationFormData>({
    resolver: zodResolver(registrationFormSchema),
    defaultValues: {
      gender: "male",
      pastActivities: [],
      question1: { question: "", answer: "" },
      question2: { question: "", answer: "" },
    },
    mode: "onChange",
  });

  const { fields, append, remove } = useFieldArray({
    control,
    name: "pastActivities",
  });

  useEffect(() => {
    const checkRegistrationStatus = async () => {
      try {
        const response = await registrationService.getRegistrationStatus();
        if (response.code === 200 && response.data) {
          setExistingRegistration(response.data);
        }
      } catch (error) {
        console.log("No existing registration");
      } finally {
        setLoading(false);
      }
    };
    checkRegistrationStatus();
  }, []);

  useEffect(() => {
    if (user?.email) {
      setValue("email", user.email);
    }
  }, [user, setValue]);

  const handleFileSelect = async (file: File) => {
    setUploadingFile(true);
    setApiError("");
    try {
      const response = await uploadService.uploadFile(file);
      if (response.code === 200) {
        setValue("resumeUrl", response.data.url);
        setUploadedFileName(file.name);
      }
    } catch (error: any) {
      setApiError(error.message || "文件上传失败");
    } finally {
      setUploadingFile(false);
    }
  };

  const handleNextStep = async () => {
    let fieldsToValidate: any[] = [];
    if (currentStep === 0) {
      fieldsToValidate = ["nickname", "fullName", "gender", "birthDate", "school", "phone", "email", "hearAbout"];
    } else if (currentStep === 1) {
      fieldsToValidate = ["technicalSkills"];
    }
    const isValid = await trigger(fieldsToValidate as any);
    if (isValid) setCurrentStep(currentStep + 1);
  };

  const onSubmit = async (values: RegistrationFormData) => {
    setApiError("");
    if (!user?.userId) {
      setApiError("用户信息不完整，请重新登录");
      return;
    }
    try {
      const note: RegistrationNote = {
        basicInfo: {
          nickname: values.nickname,
          fullName: values.fullName,
          gender: values.gender,
          birthDate: values.birthDate,
          school: values.school,
          phone: values.phone,
          email: values.email,
          resumeUrl: values.resumeUrl,
          hearAbout: values.hearAbout,
        },
        skills: {
          technicalSkills: values.technicalSkills,
          pastActivities: values.pastActivities,
          portfolioLinks: values.portfolioLinks,
        },
        reflections: {
          question1: values.question1,
          question2: values.question2,
          question3: values.question3,
        },
      };
      const response = await registrationService.submitRegistration({
        uid: user.userId,
        description: JSON.stringify(note),
      });
      if (response.code === 200 || response.code === 0) {
        setExistingRegistration(response.data);
        reset();
        setCurrentStep(0);
      }
    } catch (error: any) {
      setApiError(error.message || "报名失败，请重试");
    }
  };

  if (loading) {
    return (
      <AppTheme>
        <Container justifyContent="center" alignItems="center">
          <Typography color="white">加载中...</Typography>
        </Container>
      </AppTheme>
    );
  }

  if (existingRegistration) {
    return (
      <AppTheme>
        <Container justifyContent="center" alignItems="center">
          <Card>
            <Typography variant="h4" textAlign="center">报名状态</Typography>
            <Alert severity="success">
              <Typography variant="h6">您已成功报名 BoHack 2025！</Typography>
              <Typography variant="body2">团队名称：{existingRegistration.teamName}</Typography>
              <Typography variant="body2">角色：{existingRegistration.leader ? "队长" : "队员"}</Typography>
            </Alert>
          </Card>
        </Container>
      </AppTheme>
    );
  }

  return (
    <AppTheme>
      <Container>
        <Card>
          <Typography variant="h4" textAlign="center">报名参加 BoHack 2025</Typography>
          <Typography variant="body2" textAlign="center" color="text.secondary">
            填写以下信息，我们会在 3 个工作日内发送审核结果和下一步指引。
          </Typography>

          <Stepper activeStep={currentStep} sx={{ my: 3 }}>
            <Step><StepLabel>基本信息</StepLabel></Step>
            <Step><StepLabel>技能经历</StepLabel></Step>
            <Step><StepLabel>思考问题</StepLabel></Step>
          </Stepper>

          {apiError && <Alert severity="error">{apiError}</Alert>}

          <Box component="form" onSubmit={handleSubmit(onSubmit)} noValidate>
            {currentStep === 0 && (
              <Stack spacing={3}>
                <Box display="grid" gridTemplateColumns={{ xs: '1fr', sm: '1fr 1fr' }} gap={2}>
                  <FormControl fullWidth>
                    <FormLabel>昵称 *</FormLabel>
                    <TextField {...register("nickname")} error={!!errors.nickname} helperText={errors.nickname?.message} />
                  </FormControl>
                  <FormControl fullWidth>
                    <FormLabel>真实姓名 *</FormLabel>
                    <TextField {...register("fullName")} error={!!errors.fullName} helperText={errors.fullName?.message} />
                  </FormControl>
                  <FormControl fullWidth>
                    <FormLabel>性别 *</FormLabel>
                    <Select {...register("gender")} defaultValue="male">
                      <MenuItem value="male">男</MenuItem>
                      <MenuItem value="female">女</MenuItem>
                      <MenuItem value="other">其他</MenuItem>
                    </Select>
                  </FormControl>
                  <FormControl fullWidth>
                    <FormLabel>出生日期 *</FormLabel>
                    <TextField type="date" {...register("birthDate")} error={!!errors.birthDate} helperText={errors.birthDate?.message} />
                  </FormControl>
                  <FormControl fullWidth>
                    <FormLabel>学校 *</FormLabel>
                    <TextField {...register("school")} error={!!errors.school} helperText={errors.school?.message} />
                  </FormControl>
                  <FormControl fullWidth>
                    <FormLabel>手机号 *</FormLabel>
                    <TextField {...register("phone")} error={!!errors.phone} helperText={errors.phone?.message} />
                  </FormControl>
                  <FormControl fullWidth sx={{ gridColumn: { sm: 'span 2' } }}>
                    <FormLabel>邮箱 *</FormLabel>
                    <TextField {...register("email")} error={!!errors.email} helperText={errors.email?.message} />
                  </FormControl>
                </Box>
                <FormControl fullWidth>
                  <FormLabel>如何了解到本次活动 *</FormLabel>
                  <TextField multiline rows={3} {...register("hearAbout")} error={!!errors.hearAbout} helperText={errors.hearAbout?.message} />
                </FormControl>
                <Box display="flex" justifyContent="flex-end">
                  <Button variant="contained" onClick={handleNextStep}>下一步</Button>
                </Box>
              </Stack>
            )}

            {currentStep === 1 && (
              <Stack spacing={3}>
                <FormControl fullWidth>
                  <FormLabel>技术技能 *</FormLabel>
                  <TextField multiline rows={4} {...register("technicalSkills")} error={!!errors.technicalSkills} helperText={errors.technicalSkills?.message} />
                </FormControl>
                <Box>
                  <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
                    <FormLabel>过往活动/项目经历（可选）</FormLabel>
                    <Button size="small" startIcon={<Plus size={16} />} onClick={() => append({ name: "", description: "" })}>添加</Button>
                  </Box>
                  {fields.map((field, index) => (
                    <Box key={field.id} sx={{ p: 2, mb: 2, border: '1px solid', borderColor: 'divider', borderRadius: 1 }}>
                      <Box display="flex" justifyContent="space-between" mb={1}>
                        <Typography variant="body2">活动/项目 {index + 1}</Typography>
                        <IconButton size="small" onClick={() => remove(index)}><Trash2 size={16} /></IconButton>
                      </Box>
                      <Stack spacing={2}>
                        <TextField size="small" placeholder="活动/项目名称" {...register(`pastActivities.${index}.name`)} />
                        <TextField size="small" multiline rows={2} placeholder="描述" {...register(`pastActivities.${index}.description`)} />
                      </Stack>
                    </Box>
                  ))}
                </Box>
                <FormControl fullWidth>
                  <FormLabel>作品集链接（可选）</FormLabel>
                  <TextField multiline rows={3} {...register("portfolioLinks")} />
                </FormControl>
                <Box display="flex" justifyContent="space-between">
                  <Button variant="outlined" onClick={() => setCurrentStep(0)}>上一步</Button>
                  <Button variant="contained" onClick={handleNextStep}>下一步</Button>
                </Box>
              </Stack>
            )}

            {currentStep === 2 && (
              <Stack spacing={3}>
                <FormControl fullWidth>
                  <FormLabel>思考问题 1 *</FormLabel>
                  <Select {...register("question1.question")} defaultValue="">
                    <MenuItem value="">请选择一个问题</MenuItem>
                    {question1Options.map((q) => <MenuItem key={q} value={q}>{q}</MenuItem>)}
                  </Select>
                  <TextField multiline rows={5} sx={{ mt: 2 }} placeholder="请输入您的回答" {...register("question1.answer")} error={!!errors.question1?.answer} helperText={errors.question1?.answer?.message} />
                </FormControl>
                <FormControl fullWidth>
                  <FormLabel>思考问题 2 *</FormLabel>
                  <Select {...register("question2.question")} defaultValue="">
                    <MenuItem value="">请选择一个问题</MenuItem>
                    {question2Options.map((q) => <MenuItem key={q} value={q}>{q}</MenuItem>)}
                  </Select>
                  <TextField multiline rows={5} sx={{ mt: 2 }} placeholder="请输入您的回答" {...register("question2.answer")} error={!!errors.question2?.answer} helperText={errors.question2?.answer?.message} />
                </FormControl>
                <FormControl fullWidth>
                  <FormLabel>推荐朋友（可选）</FormLabel>
                  <TextField multiline rows={3} {...register("question3")} />
                </FormControl>
                <Box display="flex" justifyContent="space-between">
                  <Button variant="outlined" onClick={() => setCurrentStep(1)}>上一步</Button>
                  <Button variant="contained" type="submit" disabled={isSubmitting}>
                    {isSubmitting ? '提交中...' : '提交报名'}
                  </Button>
                </Box>
              </Stack>
            )}
          </Box>
        </Card>
      </Container>
    </AppTheme>
  );
};

export default RegisterPage;

