      setEmailErrorMessage(error.message || "注册失败，请重试");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline enableColorScheme />
      <SignUpContainer direction="column" justifyContent="space-between">
        <Card variant="outlined">
          <BoHackIcon />
          <Typography component="h1" variant="h4" sx={{ width: '100%', fontSize: 'clamp(2rem, 10vw, 2.15rem)' }}>
            注册
          </Typography>
          <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            <FormControl>
              <FormLabel htmlFor="name">用户名</FormLabel>
              <TextField
                autoComplete="username"
                name="name"
                required
                fullWidth
                id="name"
                placeholder="请输入用户名"
                error={nameError}
                helperText={nameErrorMessage}
                color={nameError ? 'error' : 'primary'}
              />
            </FormControl>
            <FormControl>
              <FormLabel htmlFor="email">邮箱</FormLabel>
              <TextField
                required
                fullWidth
                id="email"
                placeholder="your@email.com"
                name="email"
                autoComplete="email"
                variant="outlined"
                error={emailError}
                helperText={emailErrorMessage}
                color={emailError ? 'error' : 'primary'}
              />
            </FormControl>
            <FormControl>
              <FormLabel htmlFor="code">验证码</FormLabel>
              <Box sx={{ display: 'flex', gap: 1 }}>
                <TextField
                  required
                  id="code"
                  name="code"
                  placeholder="请输入验证码"
                  variant="outlined"
                  error={codeError}
                  helperText={codeErrorMessage}
                  color={codeError ? 'error' : 'primary'}
                  sx={{ flex: 1 }}
                />
                <Button variant="outlined" onClick={handleSendCode} disabled={countdown > 0 || sendingCode} sx={{ minWidth: '120px' }}>
                  {sendingCode ? '发送中...' : countdown > 0 ? `${countdown}秒` : '发送验证码'}
                </Button>
              </Box>
            </FormControl>
            <FormControl>
              <FormLabel htmlFor="password">密码</FormLabel>
              <TextField
                required
                fullWidth
                name="password"
                placeholder="••••••"
                type="password"
                id="password"
                autoComplete="new-password"
                variant="outlined"
                error={passwordError}
                helperText={passwordErrorMessage}
                color={passwordError ? 'error' : 'primary'}
              />
            </FormControl>
            <FormControl>
              <FormLabel htmlFor="confirmPassword">确认密码</FormLabel>
              <TextField
                required
                fullWidth
                name="confirmPassword"
                placeholder="••••••"
                type="password"
                id="confirmPassword"
                autoComplete="new-password"
                variant="outlined"
                error={confirmPasswordError}
                helperText={confirmPasswordErrorMessage}
                color={confirmPasswordError ? 'error' : 'primary'}
              />
            </FormControl>
            <Button type="submit" fullWidth variant="contained" disabled={isSubmitting}>
              {isSubmitting ? '注册中...' : '注册'}
            </Button>
          </Box>
          <Typography sx={{ textAlign: 'center' }}>
            已有账号？{' '}
            <Link href="/login" variant="body2" sx={{ alignSelf: 'center' }}>
              登录
            </Link>
          </Typography>
        </Card>
      </SignUpContainer>
    </ThemeProvider>
  );
}

import { useState } from "react";
import { useNavigate } from "react-router-dom";
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import FormLabel from '@mui/material/FormLabel';
import FormControl from '@mui/material/FormControl';
import Link from '@mui/material/Link';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import Stack from '@mui/material/Stack';
import MuiCard from '@mui/material/Card';
import { styled } from '@mui/material/styles';
import AppTheme from "../theme/AppTheme";
import { BoHackIcon } from "../components/auth/BoHackIcon";
import { authService } from "../lib/api/authService";

const Card = styled(MuiCard)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignSelf: 'center',
  width: '100%',
  padding: theme.spacing(4),
  gap: theme.spacing(2),
  margin: 'auto',
  boxShadow: 'hsla(220, 30%, 5%, 0.05) 0px 5px 15px 0px, hsla(220, 25%, 10%, 0.05) 0px 15px 35px -5px',
  [theme.breakpoints.up('sm')]: {
    maxWidth: '450px',
  },
}));

const SignUpContainer = styled(Stack)(({ theme }) => ({
  height: '100vh',
  minHeight: '100vh',
  overflow: 'hidden',
  padding: theme.spacing(2),
  [theme.breakpoints.up('sm')]: {
    padding: theme.spacing(4),
  },
  '&::before': {
    content: '""',
    display: 'block',
    position: 'absolute',
    zIndex: -1,
    inset: 0,
    backgroundImage: 'radial-gradient(at 50% 50%, hsla(210, 100%, 16%, 0.5), hsl(220, 30%, 5%))',
    backgroundRepeat: 'no-repeat',
  },
}));

export default function SignupPage() {
  const navigate = useNavigate();
  const [emailError, setEmailError] = useState(false);
  const [emailErrorMessage, setEmailErrorMessage] = useState('');
  const [passwordError, setPasswordError] = useState(false);
  const [passwordErrorMessage, setPasswordErrorMessage] = useState('');
  const [nameError, setNameError] = useState(false);
  const [nameErrorMessage, setNameErrorMessage] = useState('');
  const [confirmPasswordError, setConfirmPasswordError] = useState(false);
  const [confirmPasswordErrorMessage, setConfirmPasswordErrorMessage] = useState('');
  const [codeError, setCodeError] = useState(false);
  const [codeErrorMessage, setCodeErrorMessage] = useState('');
  const [countdown, setCountdown] = useState(0);
  const [sendingCode, setSendingCode] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSendCode = async () => {
    const email = (document.getElementById('email') as HTMLInputElement).value;
    if (!email || !/\S+@\S+\.\S+/.test(email)) {
      setEmailError(true);
      setEmailErrorMessage('请输入有效的邮箱地址');
      return;
    }

    setSendingCode(true);
    try {
      const response = await authService.sendVerificationCode(email);
      if (response.code === 200) {
        setCountdown(60);
        const timer = setInterval(() => {
          setCountdown((prev) => {
            if (prev <= 1) {
              clearInterval(timer);
              return 0;
            }
            return prev - 1;
          });
        }, 1000);
        if (response.data.dev_code) {
          setCodeErrorMessage(`开发环境验证码: ${response.data.dev_code}`);
          setCodeError(true);
        }
      }
    } catch (error: any) {
      setEmailError(true);
      setEmailErrorMessage(error.message || "发送验证码失败");
    } finally {
      setSendingCode(false);
    }
  };

  const validateInputs = () => {
    const email = document.getElementById('email') as HTMLInputElement;
    const password = document.getElementById('password') as HTMLInputElement;
    const name = document.getElementById('name') as HTMLInputElement;
    const confirmPassword = document.getElementById('confirmPassword') as HTMLInputElement;
    const code = document.getElementById('code') as HTMLInputElement;
    let isValid = true;

    if (!email.value || !/\S+@\S+\.\S+/.test(email.value)) {
      setEmailError(true);
      setEmailErrorMessage('请输入有效的邮箱地址');
      isValid = false;
    } else {
      setEmailError(false);
      setEmailErrorMessage('');
    }

    if (!password.value || password.value.length < 6) {
      setPasswordError(true);
      setPasswordErrorMessage('密码至少 6 位');
      isValid = false;
    } else {
      setPasswordError(false);
      setPasswordErrorMessage('');
    }

    if (!name.value || name.value.length < 2) {
      setNameError(true);
      setNameErrorMessage('用户名至少 2 位');
      isValid = false;
    } else {
      setNameError(false);
      setNameErrorMessage('');
    }

    if (!confirmPassword.value || confirmPassword.value !== password.value) {
      setConfirmPasswordError(true);
      setConfirmPasswordErrorMessage('两次输入的密码不一致');
      isValid = false;
    } else {
      setConfirmPasswordError(false);
      setConfirmPasswordErrorMessage('');
    }

    if (!code.value || code.value.length < 4) {
      setCodeError(true);
      setCodeErrorMessage('请输入验证码');
      isValid = false;
    } else {
      setCodeError(false);
      setCodeErrorMessage('');
    }

    return isValid;
  };

  const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    if (!validateInputs()) return;

    setIsSubmitting(true);
    const data = new FormData(event.currentTarget);
    try {
      const response = await authService.register(
        data.get('name') as string,
        data.get('email') as string,
        data.get('password') as string,
        data.get('code') as string
      );
      if (response.code === 200) {
        setTimeout(() => navigate("/login"), 3000);
      }
    } catch (error: any) {
      setEmailError(true);
      setEmailErrorMessage(error.message || "注册失败，请重试");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <AppTheme>
      <CssBaseline enableColorScheme />
      <SignUpContainer direction="column" justifyContent="space-between">
        <Card variant="outlined">
          <BoHackIcon />
          <Typography component="h1" variant="h4" sx={{ width: '100%', fontSize: 'clamp(2rem, 10vw, 2.15rem)' }}>
            注册
          </Typography>
          <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            <FormControl>
              <FormLabel htmlFor="name">用户名</FormLabel>
              <TextField
                autoComplete="username"
                name="name"
                required
                fullWidth
                id="name"
                placeholder="请输入用户名"
                error={nameError}
                helperText={nameErrorMessage}
                color={nameError ? 'error' : 'primary'}
              />
            </FormControl>
            <FormControl>
              <FormLabel htmlFor="email">邮箱</FormLabel>
              <TextField
                required
                fullWidth
                id="email"
                placeholder="your@email.com"
                name="email"
                autoComplete="email"
                variant="outlined"
                error={emailError}
                helperText={emailErrorMessage}
                color={emailError ? 'error' : 'primary'}
              />
            </FormControl>
            <FormControl>
              <FormLabel htmlFor="code">验证码</FormLabel>
              <Box sx={{ display: 'flex', gap: 1 }}>
                <TextField
                  required
                  id="code"
                  name="code"
                  placeholder="请输入验证码"
                  variant="outlined"
                  error={codeError}
                  helperText={codeErrorMessage}
                  color={codeError ? 'error' : 'primary'}
                  sx={{ flex: 1 }}
                />
                <Button variant="outlined" onClick={handleSendCode} disabled={countdown > 0 || sendingCode} sx={{ minWidth: '120px' }}>
                  {sendingCode ? '发送中...' : countdown > 0 ? `${countdown}秒` : '发送验证码'}
                </Button>
              </Box>
            </FormControl>
            <FormControl>
              <FormLabel htmlFor="password">密码</FormLabel>
              <TextField
                required
                fullWidth
                name="password"
                placeholder="••••••"
                type="password"
                id="password"
                autoComplete="new-password"
                variant="outlined"
                error={passwordError}
                helperText={passwordErrorMessage}
                color={passwordError ? 'error' : 'primary'}
              />
            </FormControl>
            <FormControl>
              <FormLabel htmlFor="confirmPassword">确认密码</FormLabel>
              <TextField
                required
                fullWidth
                name="confirmPassword"
                placeholder="••••••"
                type="password"
                id="confirmPassword"
                autoComplete="new-password"
                variant="outlined"
                error={confirmPasswordError}
                helperText={confirmPasswordErrorMessage}
                color={confirmPasswordError ? 'error' : 'primary'}
              />
            </FormControl>
            <Button type="submit" fullWidth variant="contained" disabled={isSubmitting}>
              {isSubmitting ? '注册中...' : '注册'}
            </Button>
          </Box>
          <Typography sx={{ textAlign: 'center' }}>
            已有账号？{' '}
            <Link href="/login" variant="body2" sx={{ alignSelf: 'center' }}>
              登录
            </Link>
          </Typography>
        </Card>
      </SignUpContainer>
    </ThemeProvider>
  );
}

import { useState, useEffect } from "react";
import { useForm, useFieldArray } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import Stack from '@mui/material/Stack';
import MuiCard from '@mui/material/Card';
import FormLabel from '@mui/material/FormLabel';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import IconButton from '@mui/material/IconButton';
import Alert from '@mui/material/Alert';
import Stepper from '@mui/material/Stepper';
import Step from '@mui/material/Step';
import StepLabel from '@mui/material/StepLabel';
import { styled } from '@mui/material/styles';
import { Plus, Trash2 } from "lucide-react";
import AppTheme from "../theme/AppTheme";
import { registrationService } from "../lib/api/registrationService";
import { uploadService } from "../lib/api/uploadService";
import { useAuthStore } from "../stores/authStore";
import {
  registrationFormSchema,
  question1Options,
  question2Options,
  type RegistrationFormData,
} from "../schemas/registrationSchema";
import type { RegistrationNote } from "../types/api";

const Card = styled(MuiCard)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignSelf: 'center',
  width: '100%',
  padding: theme.spacing(4),
  gap: theme.spacing(2),
  margin: 'auto',
  maxWidth: '800px',
  boxShadow: 'hsla(220, 30%, 5%, 0.05) 0px 5px 15px 0px, hsla(220, 25%, 10%, 0.05) 0px 15px 35px -5px',
}));

const Container = styled(Stack)(() => ({
  minHeight: '100vh',
  padding: '2rem',
  backgroundImage: 'radial-gradient(at 50% 50%, hsla(210, 100%, 16%, 0.5), hsl(220, 30%, 5%))',
  backgroundRepeat: 'no-repeat',
}));

const RegisterPage = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [apiError, setApiError] = useState("");
  const [existingRegistration, setExistingRegistration] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [uploadingFile, setUploadingFile] = useState(false);
  const [uploadedFileName, setUploadedFileName] = useState<string>("");
  const user = useAuthStore((state) => state.user);

  const {
    register,
    handleSubmit,
    formState: { errors, isSubmitting },
    reset,
    setValue,
    trigger,
    control,
  } = useForm<RegistrationFormData>({
    resolver: zodResolver(registrationFormSchema),
    defaultValues: {
      gender: "male",
      pastActivities: [],
      question1: { question: "", answer: "" },
      question2: { question: "", answer: "" },
    },
    mode: "onChange",
  });

  const { fields, append, remove } = useFieldArray({
    control,
    name: "pastActivities",
  });

  useEffect(() => {
    const checkRegistrationStatus = async () => {
      try {
        const response = await registrationService.getRegistrationStatus();
        if (response.code === 200 && response.data) {
          setExistingRegistration(response.data);
        }
      } catch (error) {
        console.log("No existing registration");
      } finally {
        setLoading(false);
      }
    };
    checkRegistrationStatus();
  }, []);

  useEffect(() => {
    if (user?.email) {
      setValue("email", user.email);
    }
  }, [user, setValue]);

  const handleFileSelect = async (file: File) => {
    setUploadingFile(true);
    setApiError("");
    try {
      const response = await uploadService.uploadFile(file);
      if (response.code === 200) {
        setValue("resumeUrl", response.data.url);
        setUploadedFileName(file.name);
      }
    } catch (error: any) {
      setApiError(error.message || "文件上传失败");
    } finally {
      setUploadingFile(false);
    }
  };

  const handleNextStep = async () => {
    let fieldsToValidate: any[] = [];
    if (currentStep === 0) {
      fieldsToValidate = ["nickname", "fullName", "gender", "birthDate", "school", "phone", "email", "hearAbout"];
    } else if (currentStep === 1) {
      fieldsToValidate = ["technicalSkills"];
    }
    const isValid = await trigger(fieldsToValidate as any);
    if (isValid) setCurrentStep(currentStep + 1);
  };

  const onSubmit = async (values: RegistrationFormData) => {
    setApiError("");
    if (!user?.userId) {
      setApiError("用户信息不完整，请重新登录");
      return;
    }
    try {
      const note: RegistrationNote = {
        basicInfo: {
          nickname: values.nickname,
          fullName: values.fullName,
          gender: values.gender,
          birthDate: values.birthDate,
          school: values.school,
          phone: values.phone,
          email: values.email,
          resumeUrl: values.resumeUrl,
          hearAbout: values.hearAbout,
        },
        skills: {
          technicalSkills: values.technicalSkills,
          pastActivities: values.pastActivities,
          portfolioLinks: values.portfolioLinks,
        },
        reflections: {
          question1: values.question1,
          question2: values.question2,
          question3: values.question3,
        },
      };
      const response = await registrationService.submitRegistration({
        uid: user.userId,
        description: JSON.stringify(note),
      });
      if (response.code === 200 || response.code === 0) {
        setExistingRegistration(response.data);
        reset();
        setCurrentStep(0);
      }
    } catch (error: any) {
      setApiError(error.message || "报名失败，请重试");
    }
  };

  if (loading) {
    return (
      <AppTheme>
        <Container justifyContent="center" alignItems="center">
          <Typography color="white">加载中...</Typography>
        </Container>
      </AppTheme>
    );
  }

  if (existingRegistration) {
    return (
      <AppTheme>
        <Container justifyContent="center" alignItems="center">
          <Card>
            <Typography variant="h4" textAlign="center">报名状态</Typography>
            <Alert severity="success">
              <Typography variant="h6">您已成功报名 BoHack 2025！</Typography>
              <Typography variant="body2">团队名称：{existingRegistration.teamName}</Typography>
              <Typography variant="body2">角色：{existingRegistration.leader ? "队长" : "队员"}</Typography>
            </Alert>
          </Card>
        </Container>
      </AppTheme>
    );
  }

  return (
    <AppTheme>
      <Container>
        <Card>
          <Typography variant="h4" textAlign="center">报名参加 BoHack 2025</Typography>
          <Typography variant="body2" textAlign="center" color="text.secondary">
            填写以下信息，我们会在 3 个工作日内发送审核结果和下一步指引。
          </Typography>

          <Stepper activeStep={currentStep} sx={{ my: 3 }}>
            <Step><StepLabel>基本信息</StepLabel></Step>
            <Step><StepLabel>技能经历</StepLabel></Step>
            <Step><StepLabel>思考问题</StepLabel></Step>
          </Stepper>

          {apiError && <Alert severity="error">{apiError}</Alert>}

          <Box component="form" onSubmit={handleSubmit(onSubmit)} noValidate>
            {currentStep === 0 && (
              <Stack spacing={3}>
                <Box display="grid" gridTemplateColumns={{ xs: '1fr', sm: '1fr 1fr' }} gap={2}>
                  <FormControl fullWidth>
                    <FormLabel>昵称 *</FormLabel>
                    <TextField {...register("nickname")} error={!!errors.nickname} helperText={errors.nickname?.message} />
                  </FormControl>
                  <FormControl fullWidth>
                    <FormLabel>真实姓名 *</FormLabel>
                    <TextField {...register("fullName")} error={!!errors.fullName} helperText={errors.fullName?.message} />
                  </FormControl>
                  <FormControl fullWidth>
                    <FormLabel>性别 *</FormLabel>
                    <Select {...register("gender")} defaultValue="male">
                      <MenuItem value="male">男</MenuItem>
                      <MenuItem value="female">女</MenuItem>
                      <MenuItem value="other">其他</MenuItem>
                    </Select>
                  </FormControl>
                  <FormControl fullWidth>
                    <FormLabel>出生日期 *</FormLabel>
                    <TextField type="date" {...register("birthDate")} error={!!errors.birthDate} helperText={errors.birthDate?.message} />
                  </FormControl>
                  <FormControl fullWidth>
                    <FormLabel>学校 *</FormLabel>
                    <TextField {...register("school")} error={!!errors.school} helperText={errors.school?.message} />
                  </FormControl>
                  <FormControl fullWidth>
                    <FormLabel>手机号 *</FormLabel>
                    <TextField {...register("phone")} error={!!errors.phone} helperText={errors.phone?.message} />
                  </FormControl>
                  <FormControl fullWidth sx={{ gridColumn: { sm: 'span 2' } }}>
                    <FormLabel>邮箱 *</FormLabel>
                    <TextField {...register("email")} error={!!errors.email} helperText={errors.email?.message} />
                  </FormControl>
                </Box>
                <FormControl fullWidth>
                  <FormLabel>如何了解到本次活动 *</FormLabel>
                  <TextField multiline rows={3} {...register("hearAbout")} error={!!errors.hearAbout} helperText={errors.hearAbout?.message} />
                </FormControl>
                <Box display="flex" justifyContent="flex-end">
                  <Button variant="contained" onClick={handleNextStep}>下一步</Button>
                </Box>
              </Stack>
            )}

            {currentStep === 1 && (
              <Stack spacing={3}>
                <FormControl fullWidth>
                  <FormLabel>技术技能 *</FormLabel>
                  <TextField multiline rows={4} {...register("technicalSkills")} error={!!errors.technicalSkills} helperText={errors.technicalSkills?.message} />
                </FormControl>
                <Box>
                  <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
                    <FormLabel>过往活动/项目经历（可选）</FormLabel>
                    <Button size="small" startIcon={<Plus size={16} />} onClick={() => append({ name: "", description: "" })}>添加</Button>
                  </Box>
                  {fields.map((field, index) => (
                    <Box key={field.id} sx={{ p: 2, mb: 2, border: '1px solid', borderColor: 'divider', borderRadius: 1 }}>
                      <Box display="flex" justifyContent="space-between" mb={1}>
                        <Typography variant="body2">活动/项目 {index + 1}</Typography>
                        <IconButton size="small" onClick={() => remove(index)}><Trash2 size={16} /></IconButton>
                      </Box>
                      <Stack spacing={2}>
                        <TextField size="small" placeholder="活动/项目名称" {...register(`pastActivities.${index}.name`)} />
                        <TextField size="small" multiline rows={2} placeholder="描述" {...register(`pastActivities.${index}.description`)} />
                      </Stack>
                    </Box>
                  ))}
                </Box>
                <FormControl fullWidth>
                  <FormLabel>作品集链接（可选）</FormLabel>
                  <TextField multiline rows={3} {...register("portfolioLinks")} />
                </FormControl>
                <Box display="flex" justifyContent="space-between">
                  <Button variant="outlined" onClick={() => setCurrentStep(0)}>上一步</Button>
                  <Button variant="contained" onClick={handleNextStep}>下一步</Button>
                </Box>
              </Stack>
            )}

            {currentStep === 2 && (
              <Stack spacing={3}>
                <FormControl fullWidth>
                  <FormLabel>思考问题 1 *</FormLabel>
                  <Select {...register("question1.question")} defaultValue="">
                    <MenuItem value="">请选择一个问题</MenuItem>
                    {question1Options.map((q) => <MenuItem key={q} value={q}>{q}</MenuItem>)}
                  </Select>
                  <TextField multiline rows={5} sx={{ mt: 2 }} placeholder="请输入您的回答" {...register("question1.answer")} error={!!errors.question1?.answer} helperText={errors.question1?.answer?.message} />
                </FormControl>
                <FormControl fullWidth>
                  <FormLabel>思考问题 2 *</FormLabel>
                  <Select {...register("question2.question")} defaultValue="">
                    <MenuItem value="">请选择一个问题</MenuItem>
                    {question2Options.map((q) => <MenuItem key={q} value={q}>{q}</MenuItem>)}
                  </Select>
