const Card = styled(MuiCard)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignSelf: 'center',
  width: '100%',
  padding: theme.spacing(4),
  gap: theme.spacing(2),
  margin: 'auto',
  maxWidth: '800px',
  boxShadow: 'hsla(220, 30%, 5%, 0.05) 0px 5px 15px 0px, hsla(220, 25%, 10%, 0.05) 0px 15px 35px -5px',
}));

const Container = styled(Stack)(() => ({
  minHeight: '100vh',
  padding: '2rem',
  backgroundImage: 'radial-gradient(at 50% 50%, hsla(210, 100%, 16%, 0.5), hsl(220, 30%, 5%))',
  backgroundRepeat: 'no-repeat',
}));

const RegisterPage = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [apiError, setApiError] = useState("");
  const [existingRegistration, setExistingRegistration] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [uploadingFile, setUploadingFile] = useState(false);
  const [uploadedFileName, setUploadedFileName] = useState<string>("");
  const user = useAuthStore((state) => state.user);

  const {
    register,
    handleSubmit,
    formState: { errors, isSubmitting },
    reset,
    setValue,
    trigger,
    control,
  } = useForm<RegistrationFormData>({
    resolver: zodResolver(registrationFormSchema),
    defaultValues: {
      gender: "male",
      pastActivities: [],
      question1: { question: "", answer: "" },
      question2: { question: "", answer: "" },
    },
    mode: "onChange",
  });

  const { fields, append, remove } = useFieldArray({
    control,
    name: "pastActivities",
  });

  useEffect(() => {
    const checkRegistrationStatus = async () => {
      try {
        const response = await registrationService.getRegistrationStatus();
        if (response.code === 200 && response.data) {
          setExistingRegistration(response.data);
        }
      } catch (error) {
        console.log("No existing registration");
      } finally {
        setLoading(false);
      }
    };
    checkRegistrationStatus();
  }, []);

  useEffect(() => {
    if (user?.email) {
      setValue("email", user.email);
    }
  }, [user, setValue]);

  const handleFileSelect = async (file: File) => {
    setUploadingFile(true);
    setApiError("");
    try {
      const response = await uploadService.uploadFile(file);
      if (response.code === 200) {
        setValue("resumeUrl", response.data.url);
        setUploadedFileName(file.name);
      }
    } catch (error: any) {
      setApiError(error.message || "文件上传失败");
    } finally {
      setUploadingFile(false);
    }
  };

  const handleNextStep = async () => {
    let fieldsToValidate: any[] = [];
    if (currentStep === 0) {
      fieldsToValidate = ["nickname", "fullName", "gender", "birthDate", "school", "phone", "email", "hearAbout"];
    } else if (currentStep === 1) {
      fieldsToValidate = ["technicalSkills"];
    }
    const isValid = await trigger(fieldsToValidate as any);
    if (isValid) setCurrentStep(currentStep + 1);
  };

  const onSubmit = async (values: RegistrationFormData) => {
    setApiError("");
    if (!user?.userId) {
      setApiError("用户信息不完整，请重新登录");
      return;
    }
    try {
      const note: RegistrationNote = {
        basicInfo: {
          nickname: values.nickname,
          fullName: values.fullName,
          gender: values.gender,
          birthDate: values.birthDate,
          school: values.school,
          phone: values.phone,
          email: values.email,
          resumeUrl: values.resumeUrl,
          hearAbout: values.hearAbout,
        },
        skills: {
          technicalSkills: values.technicalSkills,
          pastActivities: values.pastActivities,
          portfolioLinks: values.portfolioLinks,
        },
        reflections: {
          question1: values.question1,
          question2: values.question2,
          question3: values.question3,
        },
      };
      const response = await registrationService.submitRegistration({
        uid: user.userId,
        description: JSON.stringify(note),
      });
      if (response.code === 200 || response.code === 0) {
        setExistingRegistration(response.data);
        reset();
        setCurrentStep(0);
      }
    } catch (error: any) {
      setApiError(error.message || "报名失败，请重试");
    }
  };

  if (loading) {
    return (
      <AppTheme>
        <Container justifyContent="center" alignItems="center">
          <Typography color="white">加载中...</Typography>
        </Container>
      </AppTheme>
    );
  }

  if (existingRegistration) {
    return (
      <AppTheme>
        <Container justifyContent="center" alignItems="center">
          <Card>
            <Typography variant="h4" textAlign="center">报名状态</Typography>
            <Alert severity="success">
              <Typography variant="h6">您已成功报名 BoHack 2025！</Typography>
              <Typography variant="body2">团队名称：{existingRegistration.teamName}</Typography>
              <Typography variant="body2">角色：{existingRegistration.leader ? "队长" : "队员"}</Typography>
            </Alert>
          </Card>
        </Container>
      </AppTheme>
    );
  }

  return (
    <AppTheme>
      <Container>
        <Card>
          <Typography variant="h4" textAlign="center">报名参加 BoHack 2025</Typography>
          <Typography variant="body2" textAlign="center" color="text.secondary">
            填写以下信息，我们会在 3 个工作日内发送审核结果和下一步指引。
          </Typography>

          <Stepper activeStep={currentStep} sx={{ my: 3 }}>
            <Step><StepLabel>基本信息</StepLabel></Step>
            <Step><StepLabel>技能经历</StepLabel></Step>
            <Step><StepLabel>思考问题</StepLabel></Step>
          </Stepper>

          {apiError && <Alert severity="error">{apiError}</Alert>}

          <Box component="form" onSubmit={handleSubmit(onSubmit)} noValidate>
            {currentStep === 0 && (
              <Stack spacing={3}>
                <Box display="grid" gridTemplateColumns={{ xs: '1fr', sm: '1fr 1fr' }} gap={2}>
                  <FormControl fullWidth>
                    <FormLabel>昵称 *</FormLabel>
                    <TextField {...register("nickname")} error={!!errors.nickname} helperText={errors.nickname?.message} />
                  </FormControl>
                  <FormControl fullWidth>
                    <FormLabel>真实姓名 *</FormLabel>
                    <TextField {...register("fullName")} error={!!errors.fullName} helperText={errors.fullName?.message} />
                  </FormControl>
                  <FormControl fullWidth>
                    <FormLabel>性别 *</FormLabel>
                    <Select {...register("gender")} defaultValue="male">
                      <MenuItem value="male">男</MenuItem>
                      <MenuItem value="female">女</MenuItem>
                      <MenuItem value="other">其他</MenuItem>
                    </Select>
                  </FormControl>
                  <FormControl fullWidth>
                    <FormLabel>出生日期 *</FormLabel>
                    <TextField type="date" {...register("birthDate")} error={!!errors.birthDate} helperText={errors.birthDate?.message} />
                  </FormControl>
                  <FormControl fullWidth>
                    <FormLabel>学校 *</FormLabel>
                    <TextField {...register("school")} error={!!errors.school} helperText={errors.school?.message} />
                  </FormControl>
                  <FormControl fullWidth>
                    <FormLabel>手机号 *</FormLabel>
                    <TextField {...register("phone")} error={!!errors.phone} helperText={errors.phone?.message} />
                  </FormControl>
                  <FormControl fullWidth sx={{ gridColumn: { sm: 'span 2' } }}>
                    <FormLabel>邮箱 *</FormLabel>
                    <TextField {...register("email")} error={!!errors.email} helperText={errors.email?.message} />
                  </FormControl>
                </Box>
                <FormControl fullWidth>
                  <FormLabel>如何了解到本次活动 *</FormLabel>
                  <TextField multiline rows={3} {...register("hearAbout")} error={!!errors.hearAbout} helperText={errors.hearAbout?.message} />
                </FormControl>
                <Box display="flex" justifyContent="flex-end">
                  <Button variant="contained" onClick={handleNextStep}>下一步</Button>
                </Box>
              </Stack>
            )}

            {currentStep === 1 && (
              <Stack spacing={3}>
                <FormControl fullWidth>
                  <FormLabel>技术技能 *</FormLabel>
                  <TextField multiline rows={4} {...register("technicalSkills")} error={!!errors.technicalSkills} helperText={errors.technicalSkills?.message} />
                </FormControl>
                <Box>
                  <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
                    <Typography variant="body2">过往活动/项目经历（可选）</Typography>
                    <Button size="small" startIcon={<Plus size={16} />} onClick={() => append({ name: "", description: "" })}>添加</Button>
                  </Box>
                  {fields.map((field, index) => (
                    <Box key={field.id} sx={{ p: 2, mb: 2, border: '1px solid', borderColor: 'divider', borderRadius: 1 }}>
                      <Box display="flex" justifyContent="space-between" mb={1}>
                        <Typography variant="body2">活动/项目 {index + 1}</Typography>
                        <IconButton size="small" onClick={() => remove(index)}><Trash2 size={16} /></IconButton>
                      </Box>
                      <Stack spacing={2}>
                        <TextField size="small" placeholder="活动/项目名称" {...register(`pastActivities.${index}.name`)} />
                        <TextField size="small" placeholder="描述" multiline rows={2} {...register(`pastActivities.${index}.description`)} />
                      </Stack>
                    </Box>
                  ))}
                </Box>
                <FormControl fullWidth>
                  <FormLabel>作品集链接（可选）</FormLabel>
                  <TextField multiline rows={3} {...register("portfolioLinks")} />
                </FormControl>
                <Box display="flex" justifyContent="space-between">
                  <Button variant="outlined" onClick={() => setCurrentStep(0)}>上一步</Button>
                  <Button variant="contained" onClick={handleNextStep}>下一步</Button>
                </Box>
              </Stack>
            )}

            {currentStep === 2 && (
              <Stack spacing={3}>
                <Box>
                  <FormLabel>思考问题 1 *</FormLabel>
                  <Select {...register("question1.question")} defaultValue="" fullWidth sx={{ mb: 2 }}>
                    <MenuItem value="">请选择一个问题</MenuItem>
                    {question1Options.map((q) => <MenuItem key={q} value={q}>{q}</MenuItem>)}
                  </Select>
                  <TextField label="您的回答" multiline rows={5} fullWidth {...register("question1.answer")} error={!!errors.question1?.answer} helperText={errors.question1?.answer?.message} />
                </Box>
                <Box>
                  <FormLabel>思考问题 2 *</FormLabel>
                  <Select {...register("question2.question")} defaultValue="" fullWidth sx={{ mb: 2 }}>
                    <MenuItem value="">请选择一个问题</MenuItem>
                    {question2Options.map((q) => <MenuItem key={q} value={q}>{q}</MenuItem>)}
                  </Select>
                  <TextField label="您的回答" multiline rows={5} fullWidth {...register("question2.answer")} error={!!errors.question2?.answer} helperText={errors.question2?.answer?.message} />
                </Box>
                <TextField label="推荐朋友（可选）" multiline rows={3} {...register("question3")} />
                <Box display="flex" justifyContent="space-between">
                  <Button variant="outlined" onClick={() => setCurrentStep(1)}>上一步</Button>
                  <Button variant="contained" type="submit" disabled={isSubmitting}>
                    {isSubmitting ? '提交中...' : '提交报名'}
                  </Button>
                </Box>
              </Stack>
            )}
          </Box>
        </Card>
      </Container>
    </AppTheme>
  );
};

