import { useEffect, useRef } from "react";
import { useReducedMotion } from "framer-motion";
import gsap from "gsap";

const quotes = [
  { zh: "ã€Œä½ å¤ªå¹´è½»äº†ï¼Œåˆ«å¦„æƒ³æ”¹å˜ä»€ä¹ˆã€‚ã€", en: "You're too young to change anything." },
  { zh: "ã€Œä½ å¤ªå¹´è½»äº†ï¼Œç»éªŒå¤ªå°‘ã€‚ã€", en: "You're too young to change anything." },
  { zh: "ã€Œ42å°æ—¶ï¼Ÿä½ ä»¥ä¸ºå¥‡è¿¹èƒ½ç¼–è¯‘å‡ºæ¥ï¼Ÿã€", en: "42 hours? You think miracles can be compiled?" },
  { zh: "ã€Œåˆ›æ–°ï¼Ÿä½ è¿ç¡çœ éƒ½æ²¡ç®¡ç†å¥½ã€‚ã€", en: "Innovation? You can't even manage your sleep." },
  { zh: "ã€Œæ²¡äººä¼šåœ¨ä¹ä½ å†™çš„é‚£å‡ è¡Œä»£ç ã€‚ã€", en: "No one cares about your lines of code." },
