interface FloatingQuote {
  text: string;
  x: number;
  y: number;
  size: number;
  opacity: number;
  duration: number;
  delay: number;
}

const FloatingQuotes = () => {
  const prefersReducedMotion = useReducedMotion();
  const containerRef = useRef<HTMLDivElement>(null);
  const quotesRef = useRef<FloatingQuote[]>([]);
  const animationsRef = useRef<gsap.core.Tween[]>([]);

  useEffect(() => {
    if (prefersReducedMotion || !containerRef.current) return;

    const generateQuotes = () => {
      const floatingQuotes: FloatingQuote[] = [];
      const rows = 6; 
      const quotesPerRow = 8; 
      const totalQuotes = rows * quotesPerRow;
      const rowHeight = 100 / (rows + 1); 

      const shuffledQuotes = [...quotes].sort(() => Math.random() - 0.5);

      for (let i = 0; i < totalQuotes; i++) {
        const quote = shuffledQuotes[i % shuffledQuotes.length];
        const useEnglish = Math.random() > 0.5;

        const rowIndex = i % rows;
        const baseY = rowHeight * (rowIndex + 1);
        const randomYOffset = (Math.random() - 0.5) * 4; 

        const colIndex = Math.floor(i / rows);
        const spacingPerQuote = 130 / quotesPerRow; 
        const baseX = (colIndex * spacingPerQuote) - 15; 

        floatingQuotes.push({
          text: useEnglish ? quote.en : quote.zh,
          x: baseX,
          y: Math.max(10, Math.min(90, baseY + randomYOffset)), 
          size: Math.random() * 0.8 + 0.9, 
          opacity: Math.random() * 0.15 + 0.2,  
          duration: 40, 
          delay: 0
        });
      }

      return floatingQuotes;
    };

    quotesRef.current = generateQuotes();

    const container = containerRef.current;
    quotesRef.current.forEach((quote, index) => {
      const element = document.createElement("div");
      element.className = "floating-quote";
      element.textContent = quote.text;
      element.style.cssText = `
        position: absolute;
        left: ${quote.x}%;
        top: ${quote.y}%;
        white-space: nowrap;
        font-size: ${quote.size}em;
        opacity: ${quote.opacity};
        color: var(--text-muted, #9ca3af);
        pointer-events: none;
        font-weight: ${Math.random() > 0.6 ? '600' : '400'};
        transform: translateY(-50%);
      `;

      container.appendChild(element);

      const totalDistance = 130; 

      const animation = gsap.to(element, {
        left: `+=${totalDistance}%`, 
        duration: quote.duration,
        delay: quote.delay,
        ease: "none",
        repeat: -1,
        modifiers: {
          left: (left) => {
            const leftNum = parseFloat(left);
            if (leftNum > 115) {
              return `${leftNum - totalDistance}%`;
            }
            return `${leftNum}%`;
          }
        },
        onRepeat: () => {
          const newQuote = quotes[Math.floor(Math.random() * quotes.length)];
          const useEnglish = Math.random() > 0.5;
          element.textContent = useEnglish ? newQuote.en : newQuote.zh;
        }
      });

      animationsRef.current.push(animation);
    });

    return () => {
      animationsRef.current.forEach(animation => animation.kill());
      animationsRef.current = [];
      if (container) {
        container.innerHTML = "";
      }
    };
  }, [prefersReducedMotion]);

  if (prefersReducedMotion) return null;

  return (
    <div 
      ref={containerRef}
      className="pointer-events-none absolute inset-0 overflow-hidden"
      style={{ 
        maskImage: "linear-gradient(to right, transparent 0%, black 10%, black 90%, transparent 100%)",
        WebkitMaskImage: "linear-gradient(to right, transparent 0%, black 10%, black 90%, transparent 100%)"
      }}
    />
  );
};

export default FloatingQuotes;

