const FloatingQuotes = () => {
  const prefersReducedMotion = useReducedMotion();
  const containerRef = useRef<HTMLDivElement>(null);
  const quotesRef = useRef<FloatingQuote[]>([]);
  const occupiedYPositionsRef = useRef<Array<{ y: number; size: number }>>([]);
  const timerRef = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    if (prefersReducedMotion || !containerRef.current) return;

    const container = containerRef.current;
    const maxQuotes = 48; // æœ€å¤§å¼¹å¹•æ•°é‡
    const minFontSize = 0.6;
    const maxFontSize = 2.2;
    const minDuration = 20;
    const maxDuration = 60;

    // æ ¹æ®å­—å·è®¡ç®—é€Ÿåº¦ï¼šå¤§å­—æ…¢ï¼Œå°å­—å¿«
    const calculateDuration = (size: number): number => {
      // å­—å·è¶Šå¤§ï¼Œduration è¶Šå¤§ï¼ˆé€Ÿåº¦è¶Šæ…¢ï¼‰
      const normalized = (size - minFontSize) / (maxFontSize - minFontSize);
      return minDuration + normalized * (maxDuration - minDuration);
    };

    // æ£€æŸ¥ Y åæ ‡æ˜¯å¦ä¸ç°æœ‰å¼¹å¹•å†²çª
    const isYPositionAvailable = (y: number, size: number): boolean => {
      const buffer = size * 3; // æ ¹æ®å­—å·å¤§å°è®¾ç½®ç¼“å†²åŒº
      return !occupiedYPositionsRef.current.some(occupied => {
        const occupiedBuffer = occupied.size * 3;
        return Math.abs(y - occupied.y) < Math.max(buffer, occupiedBuffer);
      });
    };

    // ç”Ÿæˆå¯ç”¨çš„ Y åæ ‡
    const generateYPosition = (size: number, maxAttempts = 10): number => {
      for (let i = 0; i < maxAttempts; i++) {
        const y = Math.random() * 80 + 10; // 10% - 90% èŒƒå›´
        if (isYPositionAvailable(y, size)) {
          return y;
        }
      }
      // å¦‚æœå°è¯•å¤šæ¬¡ä»å¤±è´¥ï¼Œè¿”å›éšæœºä½ç½®ï¼ˆå®¹é”™å¤„ç†ï¼‰
      return Math.random() * 80 + 10;
    };

    // åˆ›å»ºå•ä¸ªå¼¹å¹•
    const createSingleQuote = () => {
      if (quotesRef.current.length >= maxQuotes) {
        // è¾¾åˆ°æœ€å¤§æ•°é‡ï¼Œç§»é™¤æœ€æ—©çš„å¼¹å¹•
        const oldestQuote = quotesRef.current.shift();
        if (oldestQuote) {
          oldestQuote.animation.kill();
          oldestQuote.element.remove();
          // ç§»é™¤å¯¹åº”çš„ Y åæ ‡å ç”¨
          occupiedYPositionsRef.current.shift();
        }
      }

      const quote = quotes[Math.floor(Math.random() * quotes.length)];
      const useEnglish = Math.random() > 0.5;
      const size = Math.random() * (maxFontSize - minFontSize) + minFontSize;
      const duration = calculateDuration(size);
      const opacity = Math.random() * 0.15 + 0.2;
      const x = -15; // ä»å·¦ä¾§å¼€å§‹
      const y = generateYPosition(size);

      // è®°å½• Y åæ ‡å ç”¨
      occupiedYPositionsRef.current.push({ y, size });

      const element = document.createElement("div");
      element.className = "floating-quote";
      element.textContent = useEnglish ? quote.en : quote.zh;
      element.style.cssText = `
        position: absolute;
        left: ${x}%;
        top: ${y}%;
        white-space: nowrap;
        font-size: ${size}em;
        opacity: ${opacity};
        color: var(--text-muted, #9ca3af);
        pointer-events: none;
        font-weight: ${Math.random() > 0.6 ? '600' : '400'};
        transform: translateY(-50%);
      `;

      container.appendChild(element);

      const totalDistance = 130;

      const animation = gsap.to(element, {
        left: `+=${totalDistance}%`,
        duration: duration,
        ease: "none",
        repeat: -1,
        modifiers: {
          left: (left) => {
            const leftNum = parseFloat(left);
            if (leftNum > 115) {
              return `${leftNum - totalDistance}%`;
            }
            return `${leftNum}%`;
          }
        },
        onRepeat: () => {
          const newQuote = quotes[Math.floor(Math.random() * quotes.length)];
          const useEnglish = Math.random() > 0.5;
          element.textContent = useEnglish ? newQuote.en : newQuote.zh;
        }
      });

      quotesRef.current.push({
        text: useEnglish ? quote.en : quote.zh,
        x,
        y,
        size,
        opacity,
        duration,
        element,
        animation
      });
    };

    // ä½¿ç”¨å®šæ—¶å™¨é€ä¸ªç”Ÿæˆå¼¹å¹•
    const startGenerating = () => {
      const generateNext = () => {
        createSingleQuote();
        // éšæœºå»¶è¿Ÿ 500-2000ms åç”Ÿæˆä¸‹ä¸€ä¸ª
        const delay = Math.random() * 1500 + 500;
        timerRef.current = setTimeout(generateNext, delay);
      };
      generateNext();
    };

    startGenerating();

    return () => {
      // æ¸…ç†å®šæ—¶å™¨
      if (timerRef.current) {
        clearTimeout(timerRef.current);
      }
      // æ¸…ç†æ‰€æœ‰å¼¹å¹•
      quotesRef.current.forEach(quote => {
        quote.animation.kill();
        quote.element.remove();
      });
      quotesRef.current = [];
      occupiedYPositionsRef.current = [];
    };
  }, [prefersReducedMotion]);

