import { useState, useEffect, useRef } from "react";
import { Link, NavLink, useLocation } from "react-router-dom";
import { Menu, X } from "lucide-react";
import Button from "../ui/Button";
import Container from "../ui/Container";
import cn from "../../utils/cn";

const navItems = [
  { label: "首页", path: "/" },
  { label: "活动日程", path: "/schedule" },
  { label: "新闻公告", path: "/news" },
  { label: "评委团队", path: "/judges" },
  { label: "赞助伙伴", path: "/sponsors" },
  { label: "FAQ", path: "/faq" },
  { label: "联系我们", path: "/contact" }
];

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isVisible, setIsVisible] = useState(true);
  const lastScrollY = useRef(0);
  const location = useLocation();

  const toggleMenu = () => setIsOpen((prev) => !prev);

  // 判断是否在首页
  const isHomePage = location.pathname === "/";

  useEffect(() => {
    const handleScroll = (e: CustomEvent) => {
      // 如果不在首页，始终显示导航栏
      if (!isHomePage) {
        setIsVisible(true);
        return;
      }

      const currentScrollY = e.detail?.scroll?.y ?? 0;

      // 首页：如果滚动位置小于 50px，隐藏导航栏
      if (currentScrollY < 50) {
        setIsVisible(false);
      } else {
        // 滚动位置大于 50px 时，显示导航栏
        setIsVisible(true);
      }

      lastScrollY.current = currentScrollY;
    };

    // 监听 Locomotive Scroll 的自定义事件
    window.addEventListener("locomotiveScroll", handleScroll as any);

    return () => {
      window.removeEventListener("locomotiveScroll", handleScroll as any);
    };
  }, [isHomePage]);

  return (
    <header
      className={cn(
        "sticky top-0 z-40 border-b border-border-main bg-bg-primary/90 backdrop-blur transition-transform duration-300",
        !isVisible && "-translate-y-full"
      )}
    >
      <Container className="flex h-16 items-center justify-between gap-6">
        <Link to="/" className="flex items-center gap-2 text-lg font-semibold">
          <span className="flex h-9 w-9 items-center justify-center rounded-lg bg-accent-blue text-base font-bold text-white">
            BH
          </span>
          <span className="text-text-head">BoHack</span>
        </Link>

        <nav className="hidden items-center gap-2 text-sm font-medium text-text-body lg:flex">
          {navItems.map((item) => (
            <NavLink
              key={item.path}
              to={item.path}
              className={({ isActive }) =>
                cn(
                  "rounded-md px-3 py-2 transition-colors hover:text-text-head",
                  isActive && "text-accent-blue"
                )
              }
            >
              {item.label}
            </NavLink>
          ))}
        </nav>

        <div className="hidden items-center gap-3 lg:flex">
          <Button variant="secondary" size="xs" asChild>
            <Link to="/login">登录</Link>
          </Button>
          <Button variant="white" size="xs" asChild>
            <Link to="/register">立即报名</Link>
          </Button>
        </div>

        <button
          type="button"
          onClick={toggleMenu}
          className="inline-flex h-10 w-10 items-center justify-center rounded-md border border-border-main text-text-head lg:hidden"
          aria-label="Toggle navigation"
        >
          {isOpen ? <X className="h-5 w-5" /> : <Menu className="h-5 w-5" />}
        </button>
      </Container>

      {isOpen && (
        <div className="border-t border-border-main bg-bg-primary pb-6 pt-3 lg:hidden">
          <Container className="flex flex-col gap-3">
            {navItems.map((item) => (
              <NavLink
                key={item.path}
                to={item.path}
                onClick={() => setIsOpen(false)}
                className={({ isActive }) =>
                  cn(
                    "rounded-md px-3 py-2 text-sm font-medium text-text-body transition-colors hover:bg-bg-secondary hover:text-text-head",
                    isActive && "text-accent-blue"
                  )
                }
              >
                {item.label}
              </NavLink>
            ))}

            <div className="mt-2 flex flex-col gap-2">
              <Button variant="secondary" className="justify-center" asChild>
                <Link to="/login" onClick={() => setIsOpen(false)}>
                  登录
                </Link>
              </Button>
              <Button variant="white" className="justify-center" asChild>
                <Link to="/register" onClick={() => setIsOpen(false)}>
                  立即报名
                </Link>
              </Button>
            </div>
          </Container>
        </div>
      )}
    </header>
  );
};

export default Navbar;

