import { Routes, Route } from "react-router-dom";
import MainLayout from "../layouts/MainLayout";
import HomePage from "../pages/HomePage";
import SchedulePage from "../pages/SchedulePage";
import NewsPage from "../pages/NewsPage";
import NewsDetailPage from "../pages/NewsDetailPage";
import JudgesPage from "../pages/JudgesPage";
import SponsorsPage from "../pages/SponsorsPage";
import FaqPage from "../pages/FaqPage";
import ContactPage from "../pages/ContactPage";
import RegisterPage from "../pages/RegisterPage";
import SignupPage from "../pages/SignupPage";
import LoginPage from "../pages/LoginPage";
import DashboardLayout from "../layouts/DashboardLayout";
import DashboardOverviewPage from "../pages/dashboard/DashboardOverviewPage";
import DashboardRegistrationPage from "../pages/dashboard/DashboardRegistrationPage";
import DashboardProjectPage from "../pages/dashboard/DashboardProjectPage";
import DashboardNotificationsPage from "../pages/dashboard/DashboardNotificationsPage";
import DashboardProfilePage from "../pages/dashboard/DashboardProfilePage";
import NotFoundPage from "../pages/NotFoundPage";
import ProtectedRoute from "../components/auth/ProtectedRoute";

const AppRoutes = () => {
  return (
    <Routes>
      <Route element={<MainLayout />}>
        <Route path="/" element={<HomePage />} />
        <Route path="/schedule" element={<SchedulePage />} />
        <Route path="/news" element={<NewsPage />} />
        <Route path="/news/:newsId" element={<NewsDetailPage />} />
        <Route path="/judges" element={<JudgesPage />} />
        <Route path="/sponsors" element={<SponsorsPage />} />
        <Route path="/faq" element={<FaqPage />} />
        <Route path="/contact" element={<ContactPage />} />
        <Route path="/register" element={<ProtectedRoute><RegisterPage /></ProtectedRoute>} />
        <Route path="/signup" element={<SignupPage />} />
        <Route path="/login" element={<LoginPage />} />
      </Route>

      <Route path="/dashboard" element={<DashboardLayout />}>
        <Route index element={<DashboardOverviewPage />} />
        <Route path="registration" element={<DashboardRegistrationPage />} />
        <Route path="project" element={<DashboardProjectPage />} />
        <Route path="notifications" element={<DashboardNotificationsPage />} />
        <Route path="profile" element={<DashboardProfilePage />} />
      </Route>

      <Route path="*" element={<NotFoundPage />} />
    </Routes>
  );
};

export default AppRoutes;
