const HomePage = () => {
  const prefersReducedMotion = useReducedMotion();
  const pageRef = useRef<HTMLDivElement | null>(null);
  const heroSectionRef = useRef<HTMLElement | null>(null);
  const heroTitleRef = useRef<HTMLHeadingElement | null>(null);
  const parallaxImageRef = useRef<HTMLImageElement | null>(null);

  useEffect(() => {
    if (prefersReducedMotion) return;

    gsap.registerPlugin(ScrollTrigger);

    let ctx: gsap.Context | null = null;

    // Add a small delay to ensure DOM is fully ready
    const timer = setTimeout(() => {
      ctx = gsap.context(() => {
        // Check if elements exist before animating
        if (heroTitleRef.current) {
          gsap.fromTo(
            heroTitleRef.current,
            { opacity: 0, y: 40 },
            {
              opacity: 1,
              y: 0,
              duration: 1.1,
              ease: "power3.out",
              scrollTrigger: {
                trigger: heroTitleRef.current,
                scroller: "[data-scroll-container]",
                start: "top 80%",
                toggleActions: "play none none none"
              }
            }
          );
        }

        if (parallaxImageRef.current) {
          gsap.to(parallaxImageRef.current, {
            y: -80,
            ease: "none",
            scrollTrigger: {
              trigger: parallaxImageRef.current,
              scroller: "[data-scroll-container]",
              start: "top bottom",
              end: "bottom top",
              scrub: true
            }
          });
        }

        const sections = gsap.utils.toArray<HTMLElement>(
          "[data-scroll-section].js-fade-section"
        );

        sections.forEach((section) => {
          if (!section || section === heroSectionRef.current) return;

          gsap.fromTo(
            section,
            { autoAlpha: 0, y: 80 },
            {
              autoAlpha: 1,
              y: 0,
              duration: 1,
              ease: "power2.out",
              scrollTrigger: {
                trigger: section,
                scroller: "[data-scroll-container]",
                start: "top 85%",
                toggleActions: "play none none reverse"
              }
            }
          );
        });
      }, pageRef);
    }, 100);

    return () => {
      clearTimeout(timer);
      if (ctx) {
        ctx.revert();
      }
      ScrollTrigger.getAll().forEach(trigger => trigger.kill());
    };
  }, [prefersReducedMotion]);

  return (
    <div ref={pageRef} className="bg-bg-primary text-text-body">
      <section
        ref={heroSectionRef}
        data-scroll-section
        className="relative flex min-h-screen items-center overflow-hidden bg-neutral-950 py-20 sm:py-28"
      >
        {/* Background Effects */}
        <div className="pointer-events-none absolute inset-0 flex items-center justify-center">
          {/* BG Effect */}
          <div className="absolute left-1/2 top-0 h-[150vh] w-[120vw] min-w-[1400px] -translate-x-1/2 opacity-10">
