import type { ReactNode } from "react";
import { motion, useReducedMotion } from "framer-motion";
import Container from "../ui/Container";
import cn from "../../utils/cn";

type PageSectionProps = {
  title?: string;
  eyebrow?: string;
  description?: string;
  align?: "left" | "center";
  children?: ReactNode;
  className?: string;
  contentClassName?: string;
  animateOnScroll?: boolean;
  revealDelay?: number;
};

const PageSection = ({
  title,
  eyebrow,
  description,
  align = "left",
  children,
  className,
  contentClassName,
  animateOnScroll = false,
  revealDelay = 0
}: PageSectionProps) => {
  const prefersReducedMotion = useReducedMotion();
  const shouldAnimate = animateOnScroll && !prefersReducedMotion;

  return (
    <motion.section
      className={cn(
        "py-16 sm:py-20 lg:py-24",
        align === "center" && "text-center",
        className
      )}
      initial={shouldAnimate ? { opacity: 0, y: 32 } : false}
      whileInView={shouldAnimate ? { opacity: 1, y: 0 } : undefined}
      viewport={shouldAnimate ? { once: true, amount: 0.2 } : undefined}
      transition={
        shouldAnimate
          ? { duration: 0.6, ease: "easeOut", delay: revealDelay }
          : undefined
      }
    >
      <Container className={contentClassName}>
        {(eyebrow || title || description) && (
          <div
            className={cn(
              "mb-10 space-y-3",
              align === "center" && "mx-auto max-w-2xl"
            )}
          >
            {eyebrow && (
              <p className="text-sm uppercase tracking-[0.2em] text-accent-purple">
                {eyebrow}
              </p>
            )}
            {title && (
              <h2 className="text-3xl font-semibold tracking-tight text-text-head sm:text-4xl">
                {title}
              </h2>
            )}
            {description && (
              <p className="text-base text-text-body sm:text-lg">
                {description}
              </p>
            )}
          </div>
        )}
        {children}
      </Container>
    </motion.section>
  );
};

export default PageSection;

