import { ReactNode } from "react";
import cn from "../../utils/cn";

type ContainerProps = {
  children: ReactNode;
  className?: string;
  size?: "default" | "wide" | "narrow";
};

const sizes = {
  default: "max-w-container",
  wide: "max-w-container-lg",
  narrow: "max-w-prose"
};

const Container = ({
  children,
  className,
  size = "default"
}: ContainerProps) => {
  return (
    <div
      className={cn(
        "mx-auto w-full px-6 sm:px-10 lg:px-12",
        sizes[size],
        className
      )}
    >
      {children}
    </div>
  );
};

export default Container;

