import { forwardRef } from "react";
import type { ButtonHTMLAttributes } from "react";
import { Slot } from "@radix-ui/react-slot";
import cn from "../../utils/cn";

type ButtonVariant = "primary" | "secondary" | "tertiary" | "white" | "outline";
type ButtonSize = "xs" | "sm" | "md" | "lg";

type ButtonProps = ButtonHTMLAttributes<HTMLButtonElement> & {
  variant?: ButtonVariant;
  size?: ButtonSize;
  isLoading?: boolean;
  asChild?: boolean;
};

const baseStyles =
  "inline-flex items-center justify-center font-semibold transition-all duration-150 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2";

const variantStyles: Record<ButtonVariant, string> = {
  primary:
    "bg-neutral-600 text-neutral-100 shadow-button hover:bg-neutral-600/90 focus-visible:outline-neutral-600",
  secondary:
    "bg-neutral-200 text-neutral-600 border border-border-light hover:bg-neutral-200/80 hover:border-neutral-600 focus-visible:outline-neutral-600",
  tertiary:
    "bg-primary-blue-light text-primary-blue hover:bg-primary-blue-light/80 focus-visible:outline-primary-blue",
  white:
    "bg-neutral-100 text-neutral-600 shadow-button hover:bg-neutral-100/90 focus-visible:outline-neutral-100",
  outline:
    "bg-transparent text-text-body border border-border-main hover:bg-bg-surface hover:border-accent-purple focus-visible:outline-accent-purple"
};

const sizeStyles: Record<ButtonSize, string> = {
  xs: "px-3 py-1.5 text-xs leading-4 rounded gap-1",
  sm: "px-3 py-3 text-xs leading-[18px] rounded gap-[3px]",
  md: "px-[18px] py-[15px] text-sm leading-5 rounded-md gap-[3px]",
  lg: "px-[22px] py-[18px] text-base leading-[22px] rounded-md gap-1"
};

const Button = forwardRef<HTMLButtonElement, ButtonProps>(
  (
    {
      className,
      variant = "primary",
      size = "md",
      isLoading = false,
      disabled,
      children,
      asChild = false,
      type,
      ...props
    },
    ref
  ) => {
    const isDisabled = disabled || isLoading;
    const Component = asChild ? Slot : "button";

    return (
      <Component
        ref={ref}
        className={cn(
          baseStyles,
          variantStyles[variant],
          sizeStyles[size],
          isLoading ? "cursor-wait opacity-80" : "hover:-translate-y-0.5",
          isDisabled && "pointer-events-none opacity-60",
          className
        )}
        disabled={isDisabled}
        type={type ?? (asChild ? undefined : "button")}
        {...props}
      >
        {children}
      </Component>
    );
  }
);

Button.displayName = "Button";

export default Button;

