import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useNavigate } from "react-router-dom";
import Container from "../components/ui/Container";
import Input from "../components/ui/Input";
import Button from "../components/ui/Button";
import { authService } from "../lib/api/authService";
import { useAuthStore } from "../stores/authStore";

const loginSchema = z.object({
  identifier: z.string().min(1, "请输入邮箱或用户名"),
  password: z.string().min(6, "密码至少 6 位")
});

type LoginForm = z.infer<typeof loginSchema>;

const LoginPage = () => {
  const navigate = useNavigate();
  const [apiError, setApiError] = useState("");
  const setAuth = useAuthStore((state) => state.setAuth);

  const {
    register,
    handleSubmit,
    formState: { errors, isSubmitting }
  } = useForm<LoginForm>({
    resolver: zodResolver(loginSchema)
  });

  const onSubmit = async (values: LoginForm) => {
    setApiError("");

    try {
      // 调用登录 API
      console.log("开始登录...", values.identifier);
      const response = await authService.login(values.identifier, values.password);
      console.log("登录响应:", response);

      if (response.code === 200 || response.code === 0) {
        // 先存储 token 到 localStorage，这样后续请求才能带上 Authorization 头
        console.log("存储 token...");
        localStorage.setItem("access_token", response.data.access_token);
        localStorage.setItem("refresh_token", response.data.refresh_token);

        // 获取用户信息
        console.log("获取用户信息...");
        const userResponse = await authService.getProfile();
        console.log("用户信息响应:", userResponse);

        if (userResponse.code === 200 || userResponse.code === 0) {
          // 存储完整的认证信息到 store
          console.log("存储认证信息到 store...");
          setAuth(response.data, userResponse.data);
          console.log("认证信息已存储，准备跳转");

          // 跳转到报名页面
          navigate("/register");
        } else {
          setApiError("获取用户信息失败");
        }
      } else {
        setApiError(response.message || "登录失败");
      }
    } catch (error: any) {
      console.error("登录错误:", error);
      setApiError(error.message || "登录失败，请检查用户名和密码");
    }
  };

  return (
    <div data-scroll-section className="bg-bg-primary py-16 sm:py-20 lg:py-24">
      <Container size="narrow" className="max-w-md">
        <div className="rounded-md border border-border-main bg-bg-surface p-10 shadow-subtle">
          <header className="mb-8 space-y-2 text-center">
            <h1 className="text-3xl font-semibold text-text-head">登录控制台</h1>
            <p className="text-sm text-text-body">
              使用注册时填写的邮箱或用户名登录，查看报名状态与项目进展。
            </p>
          </header>

          {/* 错误消息 */}
          {apiError && (
            <div className="mb-6 rounded-md border border-error-red/60 bg-error-red/10 p-4 text-sm text-error-red">
              {apiError}
            </div>
          )}

          <form className="space-y-5" onSubmit={handleSubmit(onSubmit)} noValidate>
            <div className="space-y-2">
              <label className="text-sm font-medium text-text-head">邮箱或用户名</label>
              <Input type="text" placeholder="hello@example.com 或 username" {...register("identifier")} />
              {errors.identifier && (
                <p className="text-xs text-error-red">{errors.identifier.message}</p>
              )}
            </div>
            <div className="space-y-2">
              <label className="text-sm font-medium text-text-head">密码</label>
              <Input type="password" placeholder="••••••" {...register("password")} />
              {errors.password && (
                <p className="text-xs text-error-red">{errors.password.message}</p>
              )}
            </div>
            <Button type="submit" className="w-full" isLoading={isSubmitting}>
              登录
            </Button>
          </form>
          <p className="mt-6 text-center text-sm text-text-muted">
            没有账号？<a href="/signup" className="text-accent-purple">注册</a>
          </p>
        </div>
      </Container>
    </div>
  );
};

export default LoginPage;

