import { useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import { useReducedMotion } from "framer-motion";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import { Award, Handshake, Laptop2, UsersRound, MapPin, Users, Heart } from "lucide-react";
import Button from "../components/ui/Button";
import Container from "../components/ui/Container";
import CountdownTimer from "../components/ui/CountdownTimer";
import PageSection from "../components/sections/PageSection";
import JudgeCard from "../components/sections/JudgeCard";
import SponsorCard from "../components/sections/SponsorCard";
import FeatureCard from "../components/ui/FeatureCard";
import Timeline from "../components/ui/Timeline";
import FaqAccordion from "../components/ui/FaqAccordion";

const aboutHighlights = [
  {
    title: "天津首个本地化黑客松",
    description:
      "具备强烈的城市符号和媒体关注度，立足天津，渤海起航，欢迎每一个身处天津或愿意为天津而来的创造者。",
    icon: MapPin
  },
  {
    title: "覆盖高质量参与者",
    description:
      "覆盖天津及周边城市高校学生、年轻开发者、AI初创企业等高质量参与者，汇聚创新力量。",
    icon: Users
  },
  {
    title: "纯粹平等，公益透明",
    description:
      "公益性质，财务透明，采取 Build in public 的形式，将赞助费 100% 用于参赛者和体验者，让技术和创造本身成为主角。",
    icon: Heart
  }
];

const featureItems = [
  {
    title: "丰厚奖金与荣誉",
    description:
      "总奖金池 50 万元人民币，覆盖最佳技术实现、最佳社会影响等多个奖项，团队与个人都能获得舞台与曝光。",
    icon: Award
  },
  {
    title: "行业大咖与导师",
    description:
      "来自 Stripe、Vercel、OpenAI 等公司的评委与导师全程陪伴，针对产品策略、架构设计和融资路径提供即时建议。",
    icon: UsersRound
  },
  {
    title: "技术工作坊",
    description:
      "赛前一周推出密集型 Workshop，包含 AI 应用快速落地、前端性能优化与产品叙事训练，帮助团队快速对齐方向。",
    icon: Laptop2
  },
  {
    title: "结识优秀伙伴",
    description:
      "200+ 参赛者覆盖前端、后端、设计、商业等背景，Hack 期间的社交与配对环节将促成更多长期合作。",
    icon: Handshake
  }
];

const judges = [
  {
    name: "Luna Zhang",
    title: "Stripe 资深产品技术总监",
    bio: "负责全球支付体验与新产品探索，专注打造以开发者为中心的产品生态。"
  },
  {
    name: "Leo Chen",
    title: "Linear 创始成员 / 工程负责人",
    bio: "擅长快速构建高性能前端应用，并倡导以设计驱动的团队协作模式。"
  },
  {
    name: "Mia Xu",
    title: "Rebase Capital 合伙人",
    bio: "长期关注早期创新项目投资，重点布局 AI for Good 与未来出行领域。"
  },
  {
    name: "Aaron Wu",
    title: "Vercel 亚太区解决方案架构负责人",
    bio: "致力于帮助团队打造快如即时的前端体验，并推动 Edge 架构在企业场景落地。"
  }
];

const sponsors = [
  { name: "Vercel", tier: "战略合作" },
  { name: "Stripe", tier: "铂金赞助" },
  { name: "Notion", tier: "铂金赞助" },
  { name: "OpenAI", tier: "黄金赞助" },
  { name: "Segment", tier: "黄金赞助" },
  { name: "Linear", tier: "白银赞助" }
];

const timelineSteps = [
  {
    title: "Step 1 · 立即报名",
    date: "即日起 - 2025 年 10 月 25 日",
    description: "提交个人或团队信息，选择意向赛道，我们将在 48 小时内确认参赛资格。"
  },
  {
    title: "Step 2 · 组队与创意",
    date: "2025 年 10 月 26 日 - 11 月 1 日",
    description: "参加线上配对活动，与潜在队友打磨创意，并提前预约导师方向。"
  },
  {
    title: "Step 3 · 48 小时开发",
    date: "2025 年 11 月 8 - 10 日",
    description: "在上海浦东联合办公空间集中开发，我们提供现场设备、餐饮与技术支持。"
  },
  {
    title: "Step 4 · 项目提交",
    date: "2025 年 11 月 10 日 18:00",
    description: "提交最终 Demo、文档与演示材料，评审组将进行初筛与评分。"
  },
  {
    title: "Step 5 · 路演与评审",
    date: "2025 年 11 月 10 日 19:00",
    description: "入围团队现场路演，评委提问点评，最终公布获奖名单与颁奖。"
  }
];

const faqItems = [
  {
    question: "谁可以参加 BoHack？",
    answer:
      "BoHack 欢迎开发者、设计师、产品经理及任何热爱创新的人参与。我们建议组建跨职能团队，但个人报名也可以在活动现场寻找队友。"
  },
  {
    question: "活动是线上还是线下？",
    answer:
      "本届 BoHack 全程线下举行，地点位于上海浦东创新中心。我们提供 24 小时开放的黑客空间与完善的后勤保障。"
  },
  {
    question: "需要提前准备代码或方案吗？",
    answer:
      "为了公平起见，核心代码与设计需在活动期间完成。你可以提前准备常用组件库或调研资料，现场导师会帮助你快速起步。"
  },
  {
    question: "我没有队友怎么办？",
    answer:
      "报名后可参与官方配对活动。我们还在开幕前安排破冰与创意墙，帮助你在现场快速找到志同道合的伙伴。"
  }
];

const HomePage = () => {
  const prefersReducedMotion = useReducedMotion();
  const pageRef = useRef<HTMLDivElement | null>(null);
  const heroSectionRef = useRef<HTMLElement | null>(null);
  const heroTitleRef = useRef<HTMLHeadingElement | null>(null);
  const parallaxImageRef = useRef<HTMLImageElement | null>(null);

  useEffect(() => {
    if (prefersReducedMotion) return;

    gsap.registerPlugin(ScrollTrigger);

    const ctx = gsap.context(() => {
      if (heroTitleRef.current) {
        gsap.fromTo(
          heroTitleRef.current,
          { opacity: 0, y: 40 },
          {
            opacity: 1,
            y: 0,
            duration: 1.1,
            ease: "power3.out",
            scrollTrigger: {
              trigger: heroTitleRef.current,
              scroller: "[data-scroll-container]",
              start: "top 80%",
              toggleActions: "play none none none"
            }
          }
        );
      }

      if (parallaxImageRef.current) {
        gsap.to(parallaxImageRef.current, {
          y: -80,
          ease: "none",
          scrollTrigger: {
            trigger: parallaxImageRef.current,
            scroller: "[data-scroll-container]",
            start: "top bottom",
            end: "bottom top",
            scrub: true
          }
        });
      }

      const sections = gsap.utils.toArray<HTMLElement>(
        "[data-scroll-section].js-fade-section"
      );

      sections.forEach((section) => {
        if (section === heroSectionRef.current) return;

        gsap.fromTo(
          section,
          { autoAlpha: 0, y: 80 },
          {
            autoAlpha: 1,
            y: 0,
            duration: 1,
            ease: "power2.out",
            scrollTrigger: {
              trigger: section,
              scroller: "[data-scroll-container]",
              start: "top 85%",
              toggleActions: "play none none reverse"
            }
          }
        );
      });
    }, pageRef);

    return () => {
      ctx.revert();
    };
  }, [prefersReducedMotion]);

  return (
    <div ref={pageRef} className="bg-bg-primary text-text-body">
      <section
        ref={heroSectionRef}
        data-scroll-section
        className="relative min-h-screen overflow-hidden bg-neutral-950 py-20 sm:py-28"
      >
        {/* Background Effects */}
        <div className="pointer-events-none absolute inset-0">
          {/* BG Effect */}
          <div className="absolute left-[84px] top-[-2px] h-[1762px] w-[1560px] opacity-10">
            <div className="absolute bottom-0 left-[-0.13%] right-[-0.13%] top-0">
              <img
                src="/bg-effect.svg"
                alt=""
                className="block h-full w-full max-w-none object-cover"
              />
            </div>
          </div>
          {/* Gradient & Grid */}
          <div className="absolute left-[-115px] top-[450px] h-[2219px] w-[1958px]">
            <div className="absolute inset-[-14.42%_-16.34%_-2.66%_-16.34%]">
              <img
                src="/gradient-grid.svg"
                alt=""
                className="block h-full w-full max-w-none object-cover"
              />
            </div>
          </div>
        </div>

        <Container className="relative z-10 flex flex-col items-center gap-12 text-center">
          <span
            data-scroll
            data-scroll-speed="-2"
            className="inline-flex rounded-full border border-border-main bg-bg-surface px-5 py-2 text-xs font-semibold uppercase tracking-[0.35em] text-accent-white shadow-sm"
          >
            BoHack 2025
          </span>
          <div className="space-y-6">
            <h1
              ref={heroTitleRef}
              className="text-4xl font-semibold text-text-head sm:text-6xl"
            >
              48 小时，构建未来的开发者体验
            </h1>
            <p className="mx-auto max-w-2xl text-base text-text-body sm:text-lg">
              BoHack 2025 全球创意黑客松 · 2025 年 11 月 8-10 日 · 上海浦东
            </p>
          </div>
          <CountdownTimer targetDate="2025-11-08T09:00:00+08:00" />
          <div className="flex flex-col items-center gap-3 sm:flex-row">
            <Button size="lg" asChild>
              <Link to="/register">立即报名</Link>
            </Button>
          </div>
        </Container>
      </section>

      <section data-scroll-section className="js-fade-section">
        <Container className="py-16 sm:py-20">
          <h2 className="mb-12 text-center text-3xl font-semibold text-text-head sm:text-4xl">
            关于 BoHack 2025
          </h2>
          <div className="grid gap-12 lg:grid-cols-2 lg:gap-16">
            <div className="space-y-6">
              <p className="text-base leading-relaxed text-text-body sm:text-lg">
                BoHack，取自渤海之意，是天津首个面向本地学生与市民的公益黑客松(Hackathon)，立足天津，渤海起航，欢迎每一个身处天津或愿意为天津而来的创造者。
              </p>
              <p className="text-base leading-relaxed text-text-body sm:text-lg">
                我们将采取 Build in public 的形式，透明公开化活动推进和财务流程，将赞助费 100% 用于参赛者和体验者，让技术和创造本身成为主角。
              </p>
            </div>
            <div className="space-y-6">
              <div className="flex gap-4">
                <div className="flex-shrink-0">
                  <div className="flex h-10 w-10 items-center justify-center rounded-lg bg-accent-blue/10">
                    <MapPin className="h-5 w-5 text-accent-blue" />
                  </div>
                </div>
                <div>
                  <h3 className="mb-2 font-semibold text-text-head">
                    天津首个本地化黑客松
                  </h3>
                  <p className="text-sm text-text-body">
                    具备强烈的城市符号和媒体关注度
                  </p>
                </div>
              </div>
              <div className="flex gap-4">
                <div className="flex-shrink-0">
                  <div className="flex h-10 w-10 items-center justify-center rounded-lg bg-accent-blue/10">
                    <Users className="h-5 w-5 text-accent-blue" />
                  </div>
                </div>
                <div>
                  <h3 className="mb-2 font-semibold text-text-head">
                    覆盖高质量参与者
                  </h3>
                  <p className="text-sm text-text-body">
                    覆盖天津及周边城市高校学生、年轻开发者、AI初创企业等高质量参与者
                  </p>
                </div>
              </div>
              <div className="flex gap-4">
                <div className="flex-shrink-0">
                  <div className="flex h-10 w-10 items-center justify-center rounded-lg bg-accent-blue/10">
                    <Heart className="h-5 w-5 text-accent-blue" />
                  </div>
                </div>
                <div>
                  <h3 className="mb-2 font-semibold text-text-head">
                    纯粹平等，公益透明
                  </h3>
                  <p className="text-sm text-text-body">
                    公益性质，财务透明，确保活动体验品质
                  </p>
                </div>
              </div>
            </div>
          </div>
        </Container>
      </section>

      <section data-scroll-section className="js-fade-section">
        <PageSection
          title="你将获得什么？"
          description="BoHack 的舞台为每支队伍提供资源、导师与曝光，让你的想法真正触达世界。"
        >
          <div className="grid gap-6 md:grid-cols-2">
            {featureItems.map((feature) => (
              <FeatureCard key={feature.title} {...feature} />
            ))}
          </div>
        </PageSection>
      </section>

      <section
        data-scroll-section
        className="js-fade-section bg-bg-secondary"
      >
        <Container className="flex flex-col gap-12 py-16 sm:py-20 lg:flex-row lg:items-center">
          <div className="flex-1 space-y-6">
            <h2 className="text-3xl font-semibold text-text-head sm:text-4xl">
              实地体验 · 氛围先睹为快
            </h2>
            <p className="text-base text-text-body sm:text-lg">
              黑客松核心赛区开放 24 小时，现场配备充足电源、展示区与导师休息室。ScrollTrigger
              与 Locomotive Scroll 的组合，让我们可以在网页上模拟这种富有节奏感的步调与沉浸式体验。
            </p>
          </div>
          <div className="flex-1">
            <img
              ref={parallaxImageRef}
              src="https://images.unsplash.com/photo-1523475472560-d2df97ec485c?auto=format&fit=crop&w=1400&q=80"
              alt="BoHack 黑客松现场"
              className="w-full max-w-3xl rounded-3xl shadow-2xl will-change-transform"
            />
          </div>
        </Container>
      </section>

      <section data-scroll-section className="js-fade-section">
        <PageSection
          title="重量级评委"
          description="评委团来自全球头部科技公司与投资机构，他们将以产品体验、技术深度和商业潜力等维度为你的项目评分。"
        >
          <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-4">
            {judges.map((judge) => (
              <JudgeCard key={judge.name} {...judge} />
            ))}
          </div>
        </PageSection>
      </section>

      <section
        data-scroll-section
        className="js-fade-section bg-bg-secondary"
      >
        <PageSection
          title="合作伙伴"
          description="感谢各位赞助伙伴对 BoHack 的持续支持，他们将为参赛团队提供云资源、支付服务、开发者工具与更多生态资源。"
          className="bg-transparent"
        >
          <div className="grid gap-4 sm:grid-cols-2 lg:grid-cols-3">
            {sponsors.map((sponsor) => (
              <SponsorCard key={sponsor.name} {...sponsor} />
            ))}
          </div>
        </PageSection>
      </section>

      <section data-scroll-section className="js-fade-section">
        <PageSection
          title="活动流程"
          description="简洁可依赖的五步流程，让你从报名到路演都清楚掌握关键节点。"
        >
          <Timeline steps={timelineSteps} />
        </PageSection>
      </section>

      <section
        data-scroll-section
        className="js-fade-section bg-bg-secondary"
      >
        <PageSection
          title="常见问题"
          description="提前了解参赛规则与参赛体验，确保你与队友能够专注打造最佳作品。"
          className="bg-transparent"
          contentClassName="max-w-4xl"
          align="center"
        >
          <FaqAccordion items={faqItems} />
        </PageSection>
      </section>

      <section data-scroll-section className="js-fade-section">
        <PageSection
          title="准备好接受挑战了吗？"
          description="抓住这次与全球优秀开发者同台竞技的机会，让你的创意在 48 小时内跃迁为现实。"
          align="center"
        >
          <div className="flex flex-col items-center gap-4">
            <Button size="lg" asChild>
              <Link to="/register">立即报名</Link>
            </Button>
            <p className="text-sm text-text-muted">
              名额有限，报名确认采用滚动审核机制。
            </p>
          </div>
        </PageSection>
      </section>
    </div>
  );
};

export default HomePage;

