import { ReactNode } from "react";
import cn from "../../utils/cn";

type ContainerProps = {
  children: ReactNode;
  className?: string;
  size?: "default" | "wide" | "narrow";
};

const sizes = {
  default: "max-w-container",
  wide: "max-w-container-lg",
  narrow: "max-w-prose"
};

const Container = ({
  children,
  className,
  size = "default"
}: ContainerProps) => {
  return (
    <div
      className={cn(
        "mx-auto w-full px-6 sm:px-10 lg:px-12",
        sizes[size],
        className
      )}
    >
      {children}
    </div>
  );
};

export default Container;

import type { Config } from "tailwindcss";

const config: Config = {
  darkMode: ["class"],
  content: ["./index.html", "./src/**/*.{ts,tsx}"],
  theme: {
    fontFamily: {
      sans: ["Inter", "Manrope", "ui-sans-serif", "system-ui"]
    },
    extend: {
      colors: {
        "bg-primary": "#030712",
        "bg-secondary": "#0B1120",
        "bg-surface": "#111827",
        "text-head": "#F9FAFB",
        "text-body": "#E2E8F0",
        "text-muted": "#94A3B8",
        "border-main": "#1F2937",
        "accent-purple": "#8B5CF6",
        "purple-dark": "#7C3AED",
        "accent-blue": "#2563EB",
        "blue-dark": "#1D4ED8",
        "success-green": "#22C55E",
        "error-red": "#F87171",
        neutral: {
          100: "#FFFFFF",
          200: "#F8FAFF",
          600: "#6D758F",
          950: "#0a0a0a"
        },
        primary: {
          blue: "#2388FF",
          "blue-light": "#E3EFFF"
        },
        border: {
          light: "#E1E4ED"
        }
      },
      boxShadow: {
        subtle: "0px 18px 40px rgba(8, 13, 35, 0.45)",
        button: "0px 1px 4px 0px rgba(25, 33, 61, 0.08)"
      },
      width: {
        content: "1140px"
      },
      maxWidth: {
        container: "1140px",
        "container-lg": "1280px",
        prose: "768px"
      },
      borderRadius: {
        md: "8px",
        lg: "12px"
      },
      spacing: {
        18: "4.5rem"
      }
    }
  },
  plugins: [require("@tailwindcss/forms")]
};

export default config;

