            autoAlpha: 1,
            y: 0,
            duration: 1,
            ease: "power2.out",
            scrollTrigger: {
              trigger: section,
              scroller: "[data-scroll-container]",
              start: "top 85%",
              toggleActions: "play none none reverse"
