            opacity: 1,
            y: 0,
            duration: 1.1,
            ease: "power3.out",
            scrollTrigger: {
              trigger: heroTitleRef.current,
              scroller: "[data-scroll-container]",
              start: "top 80%",
              toggleActions: "play none none none"
