import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import Container from "../components/ui/Container";
import Input from "../components/ui/Input";
import Button from "../components/ui/Button";

const loginSchema = z.object({
  email: z.string().email("请输入有效邮箱"),
  password: z.string().min(6, "密码至少 6 位")
});

type LoginForm = z.infer<typeof loginSchema>;

const LoginPage = () => {
  const {
    register,
    handleSubmit,
    formState: { errors, isSubmitting }
  } = useForm<LoginForm>({
    resolver: zodResolver(loginSchema)
  });

  const onSubmit = async (values: LoginForm) => {
    await new Promise((resolve) => setTimeout(resolve, 600));
    console.table(values);
  };

  return (
    <div data-scroll-section className="bg-bg-primary py-16 sm:py-20 lg:py-24">
      <Container size="narrow" className="max-w-md">
        <div className="rounded-md border border-border-main bg-bg-surface p-10 shadow-subtle">
          <header className="mb-8 space-y-2 text-center">
            <h1 className="text-3xl font-semibold text-text-head">登录控制台</h1>
            <p className="text-sm text-text-body">
              使用注册时填写的邮箱与密码登录，查看报名状态与项目进展。
            </p>
          </header>
          <form className="space-y-5" onSubmit={handleSubmit(onSubmit)} noValidate>
            <div className="space-y-2">
              <label className="text-sm font-medium text-text-head">邮箱</label>
              <Input type="email" placeholder="hello@example.com" {...register("email")} />
              {errors.email && (
                <p className="text-xs text-error-red">{errors.email.message}</p>
              )}
            </div>
            <div className="space-y-2">
              <label className="text-sm font-medium text-text-head">密码</label>
              <Input type="password" placeholder="••••••" {...register("password")} />
              {errors.password && (
                <p className="text-xs text-error-red">{errors.password.message}</p>
              )}
            </div>
            <Button type="submit" className="w-full" isLoading={isSubmitting}>
              登录
            </Button>
          </form>
          <p className="mt-6 text-center text-sm text-text-muted">
            没有账号？<a href="/signup" className="text-accent-purple">注册</a>
          </p>
        </div>
      </Container>
    </div>
  );
};

export default LoginPage;

