import axios, { AxiosError, InternalAxiosRequestConfig } from "axios";
import type { ApiResponse } from "../../types/api";

// 创建 axios 实例
const apiClient = axios.create({
  baseURL: import.meta.env.VITE_API_BASE_URL || "http://39.107.158.136:8000",
  timeout: 15000,
  headers: {
    "Content-Type": "application/json"
  }
});

// 请求拦截器 - 自动添加 token
apiClient.interceptors.request.use(
  (config: InternalAxiosRequestConfig) => {
    // 从 localStorage 获取 token
    const token = localStorage.getItem("access_token");

    if (token && config.headers) {
      config.headers.Authorization = `Bearer ${token}`;
    }

    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// 响应拦截器 - 统一错误处理
apiClient.interceptors.response.use(
  (response) => {
    // 直接返回 data，简化调用
    return response.data;
  },
  (error: AxiosError<ApiResponse<unknown>>) => {
    // 处理不同的错误状态
    if (error.response) {
      const { status, data } = error.response;

      switch (status) {
        case 401:
          // 未授权，清除 token 并跳转到登录页
          localStorage.removeItem("access_token");
          localStorage.removeItem("refresh_token");
          if (window.location.pathname !== "/login") {
            window.location.href = "/login";
          }
          break;
        case 403:
          console.error("403 Forbidden:", data?.message);
          break;
        case 404:
          console.error("404 Not Found:", data?.message);
          break;
        case 500:
          console.error("500 Server Error:", data?.message);
          break;
        default:
          console.error(`Error ${status}:`, data?.message);
      }

      // 返回 API 错误消息
      return Promise.reject(data || { message: "请求失败" });
    } else if (error.request) {
      // 请求已发送但没有收到响应
      console.error("Network Error:", error.message);
      return Promise.reject({ message: "网络错误，请检查网络连接" });
    } else {
      // 其他错误
      console.error("Error:", error.message);
      return Promise.reject({ message: error.message || "未知错误" });
    }
  }
);

export default apiClient;

