import { type PropsWithChildren, useEffect, useRef } from "react";
import { Outlet, useLocation } from "react-router-dom";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import LocomotiveScroll from "locomotive-scroll";
import Navbar from "../components/navigation/Navbar";
import Footer from "../components/navigation/Footer";

const MainLayout = ({ children }: PropsWithChildren) => {
  const scrollContainerRef = useRef<HTMLDivElement | null>(null);
  const locomotiveInstanceRef = useRef<LocomotiveScroll | null>(null);
  const location = useLocation();

  useEffect(() => {
    const scrollEl = scrollContainerRef.current;
    if (!scrollEl) return;

    gsap.registerPlugin(ScrollTrigger);

    const locomotive = new LocomotiveScroll({
      el: scrollEl,
      smooth: true,
      lerp: 0.08,
      reloadOnContextChange: true
    });

    locomotiveInstanceRef.current = locomotive;

    // 每当 Locomotive 滚动时，通知 ScrollTrigger 同步帧
    const handleScroll = (args: any) => {
      ScrollTrigger.update();

      // 发送自定义事件供 Navbar 监听
      window.dispatchEvent(
        new CustomEvent("locomotiveScroll", {
          detail: {
            scroll: args.scroll,
            limit: args.limit,
            currentElements: args.currentElements
          }
        })
      );
    };
    const unsubscribeScroll = locomotive.on("scroll", handleScroll);

    // ★ 核心：告诉 ScrollTrigger 使用 Locomotive Scroll 的位移
    ScrollTrigger.scrollerProxy(scrollEl, {
      scrollTop(value?: number) {
        if (typeof value === "number") {
          locomotive.scrollTo(value, { duration: 0, disableLerp: true });
        }
        const currentScroll =
          (locomotive as unknown as {
            scroll: { instance: { scroll: { y: number } } };
          })?.scroll?.instance?.scroll?.y ?? 0;
        return currentScroll;
      },
      getBoundingClientRect() {
        return {
          top: 0,
          left: 0,
          width: window.innerWidth,
          height: window.innerHeight
        };
      },
      // 当容器具备 transform 时使用 transform 固定，否则 fallback 到 fixed
      pinType: scrollEl.style.transform ? "transform" : "fixed"
    });

    const handleRefresh = () => {
      locomotive.update();
    };
    ScrollTrigger.addEventListener("refresh", handleRefresh);
    ScrollTrigger.refresh();

    // 延迟更新以确保所有内容都已加载
    setTimeout(() => {
      locomotive.update();
      ScrollTrigger.refresh();
    }, 100);

    return () => {
      ScrollTrigger.removeEventListener("refresh", handleRefresh);
      unsubscribeScroll?.();

      // 清理所有 ScrollTrigger 实例，防止残留动画引用
      ScrollTrigger.getAll().forEach((trigger) => trigger.kill());

      locomotive.destroy();
      locomotiveInstanceRef.current = null;
    };
  }, []);

  useEffect(() => {
    requestAnimationFrame(() => {
      locomotiveInstanceRef.current?.update();
    });
  }, [location.pathname, location.search, location.hash]);

  return (
    <div
      ref={scrollContainerRef}
      data-scroll-container
      className="bg-bg-primary text-text-body"
    >
      <Navbar />
      <main>
        <Outlet />
        {children}
      </main>
      <Footer />
    </div>
  );
};

export default MainLayout;

