import { useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import { useReducedMotion } from "framer-motion";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import { Award, Handshake, Laptop2, UsersRound, MapPin, Users, Heart } from "lucide-react";
import Button from "../components/ui/Button";
import Container from "../components/ui/Container";
import CountdownTimer from "../components/ui/CountdownTimer";
import PageSection from "../components/sections/PageSection";
import JudgeCard from "../components/sections/JudgeCard";
import SponsorCard from "../components/sections/SponsorCard";
import FeatureCard from "../components/ui/FeatureCard";
import Timeline from "../components/ui/Timeline";
import FaqAccordion from "../components/ui/FaqAccordion";

const aboutHighlights = [
  {
    title: "天津首个本地化黑客松",
    description:
      "具备强烈的城市符号和媒体关注度，立足天津，渤海起航，欢迎每一个身处天津或愿意为天津而来的创造者。",
    icon: MapPin
  },
  {
    title: "覆盖高质量参与者",
    description:
      "覆盖天津及周边城市高校学生、年轻开发者、AI初创企业等高质量参与者，汇聚创新力量。",
    icon: Users
  },
  {
    title: "纯粹平等，公益透明",
    description:
      "公益性质，财务透明，采取 Build in public 的形式，将赞助费 100% 用于参赛者和体验者，让技术和创造本身成为主角。",
    icon: Heart
  }
];

const featureItems = [
  {
    title: "丰厚奖金与荣誉",
    description:
      "总奖金池 50 万元人民币，覆盖最佳技术实现、最佳社会影响等多个奖项，团队与个人都能获得舞台与曝光。",
    icon: Award
  },
  {
    title: "行业大咖与导师",
    description:
      "来自 Stripe、Vercel、OpenAI 等公司的评委与导师全程陪伴，针对产品策略、架构设计和融资路径提供即时建议。",
    icon: UsersRound
  },

  return () => {
    clearTimeout(timer);
    if (ctx) {
      ctx.revert();
    }
    ScrollTrigger.getAll().forEach(trigger => trigger.kill());
  };
}, [prefersReducedMotion]);

return (
  <div ref={pageRef} className="bg-bg-primary text-text-body">
    <section
      ref={heroSectionRef}
      data-scroll-section
      className="relative flex min-h-screen items-center overflow-hidden bg-neutral-950 py-20 sm:py-28"
    >
      {/* Background Effects */}
      <div className="pointer-events-none absolute inset-0 flex items-center justify-center">
        {/* BG Effect */}
        <div className="absolute left-1/2 top-0 h-[150vh] w-[120vw] min-w-[1400px] -translate-x-1/2 opacity-10">
          <img
            src="/bg-effect.svg"
            alt=""
            className="h-full w-full object-cover object-center"
          />
        </div>
        {/* Gradient & Grid */}
        <div className="absolute left-1/2 top-[75%] h-[180vh] w-[140vw] min-w-[1600px] -translate-x-1/2 -translate-y-1/4 opacity-90">
          <img
            src="/gradient-grid.svg"
            alt=""
            className="h-full w-full object-cover object-center"
          />
        </div>
      </div>

      <Container className="relative z-10 flex w-full flex-col items-center gap-12 text-center">
        <div className="space-y-6">
          <h1
            ref={heroTitleRef}
            className="space-y-4 text-4xl font-semibold text-text-head sm:text-6xl"
          >
              <div className="text-5xl sm:text-7xl">BoHack 2025</div>
            <div>让创造驱散初冬的寒意</div>
          </h1>
          <p className="mx-auto max-w-2xl text-base text-text-body sm:text-lg">
            BoHack 2025 全球创意黑客松 · 2025 年 11 月 8-10 日 · 上海浦东
          </p>
        </div>

import { Link } from "react-router-dom";
import Container from "../components/ui/Container";

const newsList = [
  {
    id: "kickoff",
    title: "黑客松 Kickoff 全面指南",
    summary:
      "了解开幕式流程、签到须知以及组委会为你准备的资源清单，帮助团队快速进入状态。",
    publishDate: "2025-09-25"
  },
  {
    id: "mentor-office-hours",
    title: "导师 Office Hour 预约通道开启",
    summary:
