          <h1 className="text-3xl font-semibold text-text-head sm:text-4xl">
            新闻与公告
          </h1>
          <p className="max-w-2xl text-base text-text-body">
            获取 BoHack 最新动态、流程更新与重要通知。所有正式公告也会同步发送到注册邮箱。
          </p>
        </header>
        <div className="grid gap-6 md:grid-cols-2">
          {newsList.map((news) => (
            <Link
              key={news.id}
              to={`/news/${news.id}`}
              className="group rounded-md border border-border-main bg-bg-surface p-6 transition-transform duration-150 hover:-translate-y-1"
            >
              <p className="text-xs font-medium uppercase tracking-[0.2em] text-text-muted">
                {news.publishDate}
              </p>
              <h2 className="mt-3 text-xl font-semibold text-text-head group-hover:text-accent-purple">
                {news.title}
              </h2>
              <p className="mt-3 text-sm text-text-body">{news.summary}</p>
              <span className="mt-4 inline-block text-sm font-medium text-accent-purple">
                阅读详情 →
              </span>
            </Link>
          ))}
        </div>
      </Container>
    </div>
  );
};

export default NewsPage;

