import React, { useCallback, useState } from "react";
import { Upload, X, File, CheckCircle } from "lucide-react";

interface FileUploadProps {
  onFileSelect: (file: File) => void;
  onFileRemove: () => void;
  accept?: string;
  maxSize?: number; // in MB
  uploadedFileName?: string;
  isUploading?: boolean;
  error?: string;
}

const FileUpload: React.FC<FileUploadProps> = ({
  onFileSelect,
  onFileRemove,
  accept = ".pdf,.doc,.docx",
  maxSize = 10,
  uploadedFileName,
  isUploading = false,
  error,
}) => {
  const [isDragging, setIsDragging] = useState(false);

  const validateFile = (file: File): string | null => {
    // Check file size
    const fileSizeMB = file.size / (1024 * 1024);
    if (fileSizeMB > maxSize) {
      return `文件大小不能超过 ${maxSize}MB`;
    }

    // Check file type
    const acceptedTypes = accept.split(",").map((type) => type.trim());
    const fileExtension = "." + file.name.split(".").pop()?.toLowerCase();
    if (!acceptedTypes.includes(fileExtension)) {
      return `只支持 ${accept} 格式的文件`;
    }

    return null;
  };

  const handleFile = (file: File) => {
    const validationError = validateFile(file);
    if (validationError) {
      alert(validationError);
      return;
    }
    onFileSelect(file);
  };

  const handleDragEnter = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(true);
  }, []);

  const handleDragLeave = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);
  }, []);

  const handleDragOver = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
  }, []);

  const handleDrop = useCallback(
    (e: React.DragEvent) => {
      e.preventDefault();
      e.stopPropagation();
      setIsDragging(false);

      const files = e.dataTransfer.files;
      if (files && files.length > 0) {
        handleFile(files[0]);
      }
    },
    [onFileSelect]
  );

  const handleFileInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (files && files.length > 0) {
      handleFile(files[0]);
    }
  };

  return (
    <div className="w-full">
      {!uploadedFileName ? (
        <div
          onDragEnter={handleDragEnter}
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          onDrop={handleDrop}
          className={`relative flex flex-col items-center justify-center rounded-lg border-2 border-dashed px-6 py-10 transition-colors ${
            isDragging
              ? "border-accent-purple bg-accent-purple/10"
              : "border-border-primary bg-bg-surface hover:border-accent-purple/50"
          }`}
        >
          <input
            type="file"
            accept={accept}
            onChange={handleFileInput}
            className="absolute inset-0 h-full w-full cursor-pointer opacity-0"
            disabled={isUploading}
          />
          <Upload
            className={`mb-3 h-10 w-10 ${
              isDragging ? "text-accent-purple" : "text-text-muted"
            }`}
          />
          <p className="mb-1 text-sm font-medium text-text-body">
            {isUploading ? "上传中..." : "点击上传或拖拽文件到此处"}
          </p>
          <p className="text-xs text-text-muted">
            支持 {accept} 格式，最大 {maxSize}MB
          </p>
        </div>
      ) : (
        <div className="flex items-center justify-between rounded-lg border border-border-primary bg-bg-surface px-4 py-3">
          <div className="flex items-center gap-3">
            <div className="flex h-10 w-10 items-center justify-center rounded-lg bg-success-green/10">
              <CheckCircle className="h-5 w-5 text-success-green" />
            </div>
            <div>
              <p className="text-sm font-medium text-text-body">
                {uploadedFileName}
              </p>
              <p className="text-xs text-text-muted">上传成功</p>
            </div>
          </div>
          <button
            type="button"
            onClick={onFileRemove}
            className="flex h-8 w-8 items-center justify-center rounded-lg text-text-muted transition-colors hover:bg-bg-primary hover:text-error-red"
            disabled={isUploading}
          >
            <X className="h-5 w-5" />
          </button>
        </div>
      )}
      {error && <p className="mt-2 text-sm text-error-red">{error}</p>}
    </div>
  );
};

export default FileUpload;

