  userId: number;
  username: string;
  email: string;
  avatarUrl: string | null;
  bio: string | null;
  phone: string | null;
}

export interface UpdateProfileRequest {
  name?: string;
  bio?: string;
  phone?: string;
  avatarUrl?: string;
}

// Registration types
