  title: string;
  description: string;
  repoUrl: string;
  demoUrl: string;
}

export interface Project {
  projectId: number;
  userId: number;
  title: string;
  description: string;
  repoUrl: string;
  demoUrl: string;
  createdAt: string;
  updatedAt: string;
}

