import { z } from "zod";

// Page 1: Basic Information Schema
export const basicInfoSchema = z.object({
  nickname: z.string().min(1, "请输入昵称"),
  fullName: z.string().min(1, "请输入真实姓名"),
  gender: z.enum(["male", "female", "other"], {
    required_error: "请选择性别",
  }),
  birthDate: z.string().min(1, "请选择出生日期"),
  school: z.string().min(1, "请输入学校名称"),
  phone: z
    .string()
    .min(1, "请输入手机号")
    .regex(/^1[3-9]\d{9}$/, "请输入有效的手机号"),
  email: z.string().min(1, "请输入邮箱").email("请输入有效的邮箱地址"),
  resumeUrl: z.string().optional(),
  hearAbout: z.string().min(1, "请告诉我们您是如何了解到本次活动的"),
});

// Page 2: Skills Schema
export const skillsSchema = z.object({
  technicalSkills: z.string().min(1, "请输入您的技术技能"),
  pastActivities: z
    .array(
      z.object({
        name: z.string().min(1, "请输入活动/项目名称"),
        description: z.string().min(1, "请输入描述"),
      })
    )
    .optional(),
  portfolioLinks: z.string().optional(),
});

// Page 3: Reflection Questions Schema
export const reflectionSchema = z.object({
  question1: z.object({
    question: z.string().min(1, "请选择问题"),
    answer: z.string().min(1, "请回答问题"),
  }),
  question2: z.object({
    question: z.string().min(1, "请选择问题"),
    answer: z.string().min(1, "请回答问题"),
  }),
  question3: z.string().optional(),
});

// Complete Registration Form Schema
export const registrationFormSchema = z.object({
  ...basicInfoSchema.shape,
  ...skillsSchema.shape,
  ...reflectionSchema.shape,
});

export type BasicInfoFormData = z.infer<typeof basicInfoSchema>;
export type SkillsFormData = z.infer<typeof skillsSchema>;
export type ReflectionFormData = z.infer<typeof reflectionSchema>;
export type RegistrationFormData = z.infer<typeof registrationFormSchema>;

// Reflection question options
export const question1Options = [
  "AI 是否会取代人类的创造力？",
  "AI 在医疗领域的应用是否会带来伦理问题？",
  "AI 是否应该拥有法律地位？",
  "AI 是否会加剧社会不平等？",
  "AI 是否应该被用于军事目的？",
];

export const question2Options = [
  "你认为 AI 技术在未来十年内最有可能改变哪个行业？",
  "你如何看待 AI 对就业市场的影响？",
];

