import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import Container from "../components/ui/Container";
import Input from "../components/ui/Input";
import Textarea from "../components/ui/Textarea";
import Button from "../components/ui/Button";
import { registrationService } from "../lib/api/registrationService";
import { useAuthStore } from "../stores/authStore";

const registrationSchema = z.object({
  fullName: z.string().min(2, "请输入姓名"),
  email: z.string().email("请输入有效邮箱"),
  phone: z.string().min(6, "请输入联系电话"),
  role: z.enum(["engineer", "designer", "pm", "other"], {
    errorMap: () => ({ message: "请选择角色" })
  }),
  experience: z.string().min(1, "请选择经验情况"),
  techStack: z
    .string()
    .min(10, "请简单说明熟悉的技术栈或主要技能（至少 10 个字符）"),
  needTeam: z.enum(["yes", "no"]),
  motivation: z
    .string()
    .min(10, "请描述参赛动机（至少 10 个字符）")
    .max(800, "请限制在 800 字以内"),
  agreeTerms: z.literal(true, {
    errorMap: () => ({ message: "请阅读并同意参赛协议" })
  })
});

type RegistrationForm = z.infer<typeof registrationSchema>;

const RegisterPage = () => {
  const [submitted, setSubmitted] = useState(false);
  const [apiError, setApiError] = useState("");
  const [existingRegistration, setExistingRegistration] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const user = useAuthStore((state) => state.user);

  const {
    register,
    handleSubmit,
    formState: { errors, isSubmitting },
    reset,
    setValue
  } = useForm<RegistrationForm>({
    resolver: zodResolver(registrationSchema),
    defaultValues: {
      needTeam: "no",
      role: "engineer",
      experience: "1-3"
    }
  });

  // 检查是否已报名
  useEffect(() => {
    const checkRegistrationStatus = async () => {
      try {
        const response = await registrationService.getRegistrationStatus();
        if (response.code === 200 && response.data) {
          setExistingRegistration(response.data);
        }
      } catch (error) {
        // 未报名或其他错误，忽略
        console.log("No existing registration");
      } finally {
        setLoading(false);
      }
    };

    checkRegistrationStatus();
  }, []);

  // 自动填充用户邮箱
  useEffect(() => {
    if (user?.email) {
      setValue("email", user.email);
    }
  }, [user, setValue]);

  const onSubmit = async (values: RegistrationForm) => {
    setApiError("");

    try {
      // 将表单字段映射到 API 格式
      const apiData = {
        teamName: values.fullName, // 使用姓名作为团队名
        leader: values.needTeam === "no", // 不需要队友 = 是队长
        note: `
角色: ${getRoleLabel(values.role)}
经验: ${values.experience}
联系方式: ${values.phone}

技术栈/专长:
${values.techStack}

参赛动机:
${values.motivation}
        `.trim()
      };

      const response = await registrationService.submitRegistration(apiData);

      if (response.code === 200) {
        setSubmitted(true);
        setExistingRegistration(response.data);
        reset();
      }
    } catch (error: any) {
      setApiError(error.message || "报名失败，请重试");
    }
  };

  // 角色标签映射
  const getRoleLabel = (role: string) => {
    const labels: Record<string, string> = {
      engineer: "工程师 / 开发者",
      designer: "设计师",
      pm: "产品经理",
      other: "其他"
    };
    return labels[role] || role;
  };

  // 如果正在加载
  if (loading) {
    return (
      <div data-scroll-section className="bg-bg-secondary py-16 sm:py-20 lg:py-24">
        <Container size="narrow">
          <div className="rounded-md border border-border-main bg-bg-surface p-10 text-center shadow-subtle">
            <p className="text-text-body">加载中...</p>
          </div>
        </Container>
      </div>
    );
  }

  // 如果已经报名
  if (existingRegistration && !submitted) {
    return (
      <div data-scroll-section className="bg-bg-secondary py-16 sm:py-20 lg:py-24">
        <Container size="narrow">
          <div className="rounded-md border border-border-main bg-bg-surface p-10 shadow-subtle">
            <header className="mb-8 space-y-3 text-center">
              <h1 className="text-3xl font-semibold text-text-head sm:text-4xl">
                报名状态
              </h1>
            </header>
            <div className="space-y-6">
              <div className="rounded-md border border-success-green/60 bg-success-green/10 p-6">
                <h2 className="mb-4 text-lg font-semibold text-success-green">
                  您已成功报名 BoHack 2025！
                </h2>
                <div className="space-y-2 text-sm text-text-body">
                  <p><strong>团队名称：</strong>{existingRegistration.teamName}</p>
                  <p><strong>角色：</strong>{existingRegistration.leader ? "队长" : "队员"}</p>
                  {existingRegistration.status && (
                    <p><strong>状态：</strong>{existingRegistration.status}</p>
                  )}
                </div>
              </div>
              <p className="text-center text-sm text-text-muted">
                请留意邮箱，组委会将会尽快与你联系。
              </p>
            </div>
          </div>
        </Container>
      </div>
    );
  }

  return (
    <div data-scroll-section className="bg-bg-secondary py-16 sm:py-20 lg:py-24">
      <Container size="narrow">
        <div className="rounded-md border border-border-main bg-bg-surface p-10 shadow-subtle">
          <header className="mb-10 space-y-3 text-center">
            <h1 className="text-3xl font-semibold text-text-head sm:text-4xl">
              报名参加 BoHack 2025
            </h1>
            <p className="text-base text-text-body">
              填写以下信息，我们会在 3 个工作日内发送审核结果和下一步指引。
            </p>
          </header>

          {/* 成功消息 */}
          {submitted && (
            <div className="mb-8 rounded-md border border-success-green/60 bg-success-green/10 p-4 text-sm text-success-green">
              报名信息已提交成功！请留意邮箱，组委会将会尽快与你联系。
            </div>
          )}

          {/* 错误消息 */}
          {apiError && (
            <div className="mb-8 rounded-md border border-error-red/60 bg-error-red/10 p-4 text-sm text-error-red">
              {apiError}
            </div>
          )}
          <form
            className="space-y-8"
            onSubmit={handleSubmit(onSubmit)}
            noValidate
          >
            <section className="space-y-6">
              <h2 className="text-xl font-semibold text-text-head">基本信息</h2>
              <div className="grid gap-6 sm:grid-cols-2">
                <div className="space-y-2">
                  <label className="text-sm font-medium text-text-head">
                    姓名
                  </label>
                  <Input placeholder="你的姓名" {...register("fullName")} />
                  {errors.fullName && (
                    <p className="text-xs text-error-red">
                      {errors.fullName.message}
                    </p>
                  )}
                </div>
                <div className="space-y-2">
                  <label className="text-sm font-medium text-text-head">
                    邮箱
                  </label>
                  <Input placeholder="hello@example.com" {...register("email")} />
                  {errors.email && (
                    <p className="text-xs text-error-red">
                      {errors.email.message}
                    </p>
                  )}
                </div>
                <div className="space-y-2">
                  <label className="text-sm font-medium text-text-head">
                    手机 / 联系方式
                  </label>
                  <Input placeholder="+86 138 0000 0000" {...register("phone")} />
                  {errors.phone && (
                    <p className="text-xs text-error-red">
                      {errors.phone.message}
                    </p>
                  )}
                </div>
                <div className="space-y-2">
                  <label className="text-sm font-medium text-text-head">
                    参赛角色
                  </label>
                  <select
                    className="h-12 w-full rounded-md border border-border-main bg-bg-surface px-4 text-sm text-text-body focus:border-accent-purple focus:outline-none focus:ring-0"
                    {...register("role")}
                  >
                    <option value="engineer">工程师 / 开发者</option>
                    <option value="designer">设计师</option>
                    <option value="pm">产品经理</option>
                    <option value="other">其他</option>
                  </select>
                  {errors.role && (
                    <p className="text-xs text-error-red">{errors.role.message}</p>
                  )}
                </div>
                <div className="space-y-2">
                  <label className="text-sm font-medium text-text-head">
                    工作 / 学习经验
                  </label>
                  <select
                    className="h-12 w-full rounded-md border border-border-main bg-bg-surface px-4 text-sm text-text-body focus:border-accent-purple focus:outline-none focus:ring-0"
                    {...register("experience")}
                  >
                    <option value="0-1">0-1 年</option>
                    <option value="1-3">1-3 年</option>
                    <option value="3-5">3-5 年</option>
                    <option value="5+">5 年以上</option>
                  </select>
                  {errors.experience && (
                    <p className="text-xs text-error-red">
                      {errors.experience.message}
                    </p>
                  )}
                </div>
                <div className="space-y-2">
                  <label className="text-sm font-medium text-text-head">
                    是否需要组队
                  </label>
                  <div className="flex gap-4 rounded-md border border-border-main bg-bg-surface px-4 py-3 text-sm">
                    <label className="flex items-center gap-2">
                      <input
                        type="radio"
                        value="yes"
                        {...register("needTeam")}
                        className="h-4 w-4 border-border-main bg-bg-surface text-accent-purple focus:ring-accent-purple"
                      />
                      需要队友
                    </label>
                    <label className="flex items-center gap-2">
                      <input
                        type="radio"
                        value="no"
                        {...register("needTeam")}
                        className="h-4 w-4 border-border-main bg-bg-surface text-accent-purple focus:ring-accent-purple"
                      />
                      已有团队
                    </label>
                  </div>
                  {errors.needTeam && (
                    <p className="text-xs text-error-red">
                      {errors.needTeam.message}
                    </p>
                  )}
                </div>
              </div>
            </section>

            <section className="space-y-6">
              <h2 className="text-xl font-semibold text-text-head">参赛信息</h2>
              <div className="space-y-4">
                <div className="space-y-2">
                  <label className="text-sm font-medium text-text-head">
                    技术栈 / 专长
                  </label>
                  <Textarea
                    rows={4}
                    placeholder="请介绍你熟悉的技术、工具或作品链接"
                    {...register("techStack")}
                  />
                  {errors.techStack && (
                    <p className="text-xs text-error-red">
                      {errors.techStack.message}
                    </p>
                  )}
                </div>
                <div className="space-y-2">
                  <label className="text-sm font-medium text-text-head">
                    参赛动机 / 期待
                  </label>
                  <Textarea
                    rows={5}
                    placeholder="你希望在 BoHack 完成什么？任何值得分享的故事都欢迎在此写下。"
                    {...register("motivation")}
                  />
                  {errors.motivation && (
                    <p className="text-xs text-error-red">
                      {errors.motivation.message}
                    </p>
                  )}
                </div>
              </div>
            </section>

            <div className="flex items-start gap-3 rounded-md border border-border-main bg-bg-surface px-4 py-3 text-sm">
              <input
                type="checkbox"
                className="mt-1 h-4 w-4 border-border-main bg-bg-surface text-accent-purple focus:ring-accent-purple"
                {...register("agreeTerms")}
              />
              <p>
                我已阅读并同意
                <a href="#" className="text-accent-purple">
                  《BoHack 参赛条款》
                </a>
                ，承诺遵守活动规则与行为准则。
              </p>
            </div>
            {errors.agreeTerms && (
              <p className="text-xs text-error-red">{errors.agreeTerms.message}</p>
            )}

            <div className="flex justify-end">
              <Button type="submit" size="lg" isLoading={isSubmitting}>
                提交报名
              </Button>
            </div>
          </form>
        </div>
      </Container>
    </div>
  );
};

export default RegisterPage;

